INSERT INTO `studijny_program` (`id`, `fakulta_ID`, `nazov`, `prijimacie_skusky`, `ponuka_externeho_studia`, `vyucovaci_jazyk`, `datum_aktualizacie_clanku`, `clanok`) VALUES
(1, 1, 'Aplikovaná informatika', 'Nie', 'Nie', 'Slovenský', '26.4.2023', '<p style=\"margin-left:40px; text-align:justify\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">&bdquo;Štúdiom tohto programu získaš okrem teoreticko-metodických zručností najmä praktickú zručnosť z architektúry počítačov, operačných systémov, algoritmizácie, programovania a zberu &bdquo;big data&ldquo;. Dosiahneš to štúdiom základných predmetov z informačných systémov a softvérového inžinierstva, ktoré sa prelínajú s kurzami v dvoch hlavných aplikačných oblastiach: bezpečnosť informačných systémov a modelovanie a simulácia udalostných systémov. Potom nájdeš uplatnenie v auditovaní veľkých informačných a komunikačných systémov, ako napr. elektronické bankovníctvo, ochrana citlivých dát, modelovanie work-flow procesov a vnorených systémov, pri návrhu informačných systémov a softvérových produktov všeobecne, ako aj v systémoch riadenia. Budeš pripravený pracovať s citlivými dátami, ako aj ich ochrániť na rôznych platformách.&ldquo;<span style=\"font-size:11pt\"><span style=\"font-size:10.0pt\"><span style=\"color:#333333\"> &nbsp;</span><a href=\"https://www.fei.stuba.sk/sk/uchadzacov.html?page_id=295\" target=\"_blank\"><span style=\"color:#4e5f70\">zdroj</span></a></span></span></span></p><p style=\"margin-left:120px; text-align:justify\">&nbsp;</p><p style=\"margin-left:40px; text-align:justify\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">&bdquo;Naučíme ťa všetko o&nbsp;programovaní, operačných a&nbsp;databázových systémoch, algoritmoch či zbere a&nbsp;spracované dát. Po absolvovaní budeš vedieť programovať v&nbsp;C, C++, Jave, Pythone, HTML5, PHP, Javascripte aj Ajaxe (ale že si vďaka tomu nájdeš frajera/frajerku ti sľúbiť nemôžeme). Dnes sa už žiadna spoločnosť nezaobíde bez dobrého CRM-ka, cloudového riešenia alebo šikovného cyber sekuriťáka &ndash; o&nbsp;nápadníkov na trhu práce rozhodne nebudeš mať núdzu.</span></p><p style=\"margin-left:40px; text-align:justify\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Naši absolventi sa často uplatňujú v&nbsp;technologických spoločnostiach ako špecialisti IT bezpečnosti alebo IT architekti, najväčší počet z&nbsp;nich však kódi weby, appky a&nbsp;AI v&nbsp;startupoch, o&nbsp;ktorých si už určite počul. Staň sa jedným z&nbsp;nich a&nbsp;podaj si prihlášku. Just do IT.&ldquo;</span> <span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"font-size:11pt\"><span style=\"font-size:10.0pt\">&nbsp;</span></span><a href=\"https://www.matozmysel.sk/studijne-programy/\" target=\"_blank\"><span style=\"color:#4e5f70\"><span style=\"font-size:11pt\"><span style=\"font-size:10.0pt\">zdroj</span></span></span></a></span></p><p style=\"margin-left:40px; text-align:justify\">&nbsp;</p><p style=\"margin-left:40px; text-align:justify\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Ako študent FEI STU máš samozrejme možnosť absolvovať časť štúdia v&nbsp;zahraničí v&nbsp;rámci programu Erasmus+ - študijný pobyt. Na výber máš z&nbsp;mnoha univerzít po celom svete, stačí si len podať žiadosť, splniť pár formalít a&nbsp;môžeš sa na 3 až 12 mesiacov vrhnúť do sveta.</span></p><p style=\"margin-left:40px; text-align:justify\">&nbsp;</p><p style=\"margin-left:40px; text-align:justify\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Vyššie uvedený študijný program fakulta ponúka výhradne v&nbsp;slovenskom jazyku. V&nbsp;dennej prezenčnej forme je dĺžka štúdia štandardná &ndash; 3 roky. </span></p><p style=\"margin-left:40px; text-align:center\">&nbsp;</p><p style=\"margin-left:40px; text-align:center\"><iframe allow=\"accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture; web-share\" allowfullscreen=\"\" frameborder=\"0\" height=\"315\" src=\"https://www.youtube-nocookie.com/embed/Kqsx30VPP7o\" title=\"YouTube video player\" width=\"560\"></iframe></p><p style=\"margin-left:120px; text-align:justify\">&nbsp;</p><p style=\"margin-left:40px; text-align:justify\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Ak ťa zaujal práve tento študijný odbor, viac sa o ňom dočítaš <strong><a href=\"https://uim.fei.stuba.sk/bakalarske-studium/\" target=\"_blank\"><span style=\"color:#16a085\">tu</span></a></strong>. </span></p><p style=\"margin-left:40px; text-align:justify\">&nbsp;</p><p style=\"margin-left:40px; text-align:justify\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><strong><a href=\"https://www.fei.stuba.sk/buxus/docs/studium_od_2023/SP_2023_2024_v2.pdf\" target=\"_blank\"><span style=\"color:#16a085\">Študijný plán</span></a></strong></span></p><p style=\"margin-left:40px; text-align:justify\"><strong><span style=\"font-family:Tahoma,Geneva,sans-serif\"><a href=\"https://www.fei.stuba.sk/sk/uchadzacov/prijimacie-konanie-na-bakalarske-studium-na-akademicky-rok-2023-2024.html?page_id=3759\" target=\"_blank\"><span style=\"color:#16a085\">Informácie ku prijímaciemu konaniu</span></a></span></strong></p>'),
(2, 1, 'Robotika a kybernetika', 'Nie', 'Nie', 'Slovenský', '22.4.2023', '<p style=\"margin-left:40px; text-align:justify\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">&bdquo;Ak čakáš, že ťa naučíme, ako ovládnuť svet pomocou strojov, sklameme ťa. Na žiadnu apokalypsu ťa u&nbsp;nás pripravovať nebudeme. Na robotike a&nbsp;kybernetike ti však odovzdáme vedomosti o&nbsp;prevádzke a&nbsp;programovaní systémov automatizácia, vďaka ktorým fungujú dopravné systémy, energetická infraštruktúra či zdravotníctvo. Poď s&nbsp;nami navrhovať výrobné procesy, vytvárať mobilné aplikácie, stavať robotov, programovať a&nbsp;udržiavať systémy priemyselnej informatiky... a&nbsp;keď pri tom všetkom zostane čas na vojnu robotov, Matrix maratóny a&nbsp;súťaže o&nbsp;lístky na Comic-Con, good for you.&ldquo;<span style=\"font-size:11px\"> </span><span style=\"font-size:12px\"><a href=\"https://www.matozmysel.sk/studijne-programy/\" target=\"_blank\"><span style=\"color:#2c3e50\">Zdroj</span></a></span></span></p><p style=\"margin-left:40px; text-align:justify\">&nbsp;</p><p style=\"margin-left:40px; text-align:justify\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">&bdquo;Ako funguje robotický vysávač? Ako zrýchliť proces výroby a eliminovať chybovosť ľudského faktoru? Ako funguje bezpilotné lietadlo? Kedy budeme bežne používať autá bez vodičov? Má umelá inteligencia svoje limity?<br />\r\nŠtúdium Robotiky a kybernetiky ti zodpovie tieto a ďalšie množstvo otázok z vybranej problematiky. Počas štúdia pochopíš, ako fungujú sofistikované stroje v priemysle, navigačné prístroje vo vesmírnych centrách, aké sú princípy fungovania inteligentnej domácnosti. Ako je možné, že robot vyrieši náhodnú úlohu v reálnom čase? .&ldquo;<span style=\"font-size:11px\"> </span><span style=\"font-size:12px\"><a href=\"https://www.fei.stuba.sk/sk/uchadzacov.html?page_id=295\" target=\"_blank\"><span style=\"color:#2c3e50\">Zdroj</span></a></span></span></p><p style=\"margin-left:40px; text-align:justify\">&nbsp;</p><p style=\"margin-left:40px; text-align:justify\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Ako študent FEI STU máš samozrejme možnosť absolvovať časť štúdia v&nbsp;zahraničí v&nbsp;rámci programu Erasmus+ - študijný pobyt. Na výber máš z&nbsp;mnoha univerzít po celom svete, stačí si len podať žiadosť, splniť pár formalít a&nbsp;môžeš sa na 3 až 12 mesiacov vrhnúť do sveta.</span></p><p style=\"margin-left:40px\">&nbsp;</p><p style=\"margin-left:40px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Vyššie uvedený študijný program fakulta ponúka výhradne v&nbsp;slovenskom jazyku. V&nbsp;dennej prezenčnej forme je dĺžka štúdia štandardná &ndash; 3 roky.</span></p><p style=\"margin-left:40px\">&nbsp;</p><p style=\"margin-left:40px; text-align:center\"><iframe allow=\"accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture; web-share\" allowfullscreen=\"\" frameborder=\"0\" height=\"315\" src=\"https://www.youtube-nocookie.com/embed/aiS3wwbdJqs\" title=\"YouTube video player\" width=\"560\"></iframe></p><p style=\"margin-left:40px\">&nbsp;</p><p style=\"margin-left:40px\"><strong><span style=\"font-family:Tahoma,Geneva,sans-serif\"><a href=\"https://www.fei.stuba.sk/buxus/docs/studium_od_2023/SP_2023_2024_v2.pdf\" target=\"_blank\"><span style=\"color:#16a085\">Študijný plán</span></a></span></strong></p><p style=\"margin-left:40px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><strong><a href=\"https://www.fei.stuba.sk/sk/uchadzacov/prijimacie-konanie-na-bakalarske-studium-na-akademicky-rok-2023-2024.html?page_id=3759#stud\" target=\"_blank\"><span style=\"color:#16a085\">Informácie ku prijímaciemu konaniu</span></a></strong></span></p><p style=\"margin-left:40px\">&nbsp;</p>'),
(3, 1, 'Informačné a komunikačné technológie', 'Nie', 'Nie', 'Slovenský', '22.4.2023', '<p style=\"margin-left:40px; text-align:justify\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">&bdquo;Nezáleží na tom, či ťa viac zaujíma kompresia obrazu, návrh sieťovej infraštruktúry alebo systémy autonómnych vozidiel. Schopnosti a&nbsp;vedomosti, ktoré si vybuduješ, využiješ v&nbsp;spoločnostiach zaoberajúcich sa informačnými a&nbsp;komunikačnými technolófiami, multimediálnymi službami či zložitými IT systémami. Ak vieš, že sa nemusíš báť 5G, prihlás sa na študijný program Informačné a&nbsp;komunikačné technológie a&nbsp;buď tým, kto konečne vynájde teleport.&ldquo; <span style=\"font-size:12px\"><a href=\"https://www.matozmysel.sk/studijne-programy/\" target=\"_blank\"><span style=\"color:#4e5f70\">Zdroj</span></a></span></span></p><p style=\"margin-left:40px; text-align:justify\">&nbsp;</p><p style=\"margin-left:40px; text-align:justify\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Ani Ty si nevieš predstaviť každodenné fungovanie bez telefónu a internetu? Vytočíš číslo a takmer okamžite sa Ti ozve hlas osoby, ktorá je na inom kontinente. Vďaka internetu vieš za zlomok sekundy preniesť obrovské kvantum dát cez polovicu zemegule a pritom si spravil iba jedno malé &bdquo;klik&ldquo;. Ako fungujú tieto fascinujúce technológie, ktoré dnes považujeme za samozrejmosť?<br />\r\nVďaka štúdiu Telekomunikácií sa dozvieš, ako sa hlasový signál, videá a obrázky šíria z Tvojho smartfónu do telefónu Tvojho kamaráta. Spoznáš, ako funguje satelitné vysielanie a na čo sú dobré IP adresy. Zistíš, ako zakódovať video tak, aby nestratilo na kvalite, ale pritom výrazne schudlo. Zapojíš sa do zaujímavých výskumov, kde budeme spoločne vytvárať nové technológie. Vďaka bohatej praxi, v rámci ktorej môžeš rozvíjať vlastné nápady, sa uplatníš ako žiadaný odborník, a to nielen na Slovensku, ale aj v zahraničí. <a href=\"https://www.fei.stuba.sk/sk/uchadzacov.html?page_id=295\" target=\"_blank\"><span style=\"font-size:12px\"><span style=\"color:#2c3e50\">Zdroj</span></span></a></span></p><p style=\"margin-left:40px; text-align:justify\">&nbsp;</p><p style=\"margin-left:40px; text-align:justify\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Ako študent FEI STU máš samozrejme možnosť absolvovať časť štúdia v&nbsp;zahraničí v&nbsp;rámci programu Erasmus+ - študijný pobyt. Na výber máš z&nbsp;mnoha univerzít po celom svete, stačí si len podať žiadosť, splniť pár formalít a&nbsp;môžeš sa na 3 až 12 mesiacov vrhnúť do sveta.</span></p><p style=\"margin-left:40px; text-align:justify\">&nbsp;</p><p style=\"margin-left:40px; text-align:justify\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Vyššie uvedený študijný program fakulta ponúka výhradne v&nbsp;slovenskom jazyku. V&nbsp;dennej prezenčnej forme je dĺžka štúdia štandardná &ndash; 3 roky. </span></p><p style=\"margin-left:40px; text-align:justify\">&nbsp;</p><p style=\"margin-left:40px; text-align:justify\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><a href=\"https://umikt.sk/uchadzaci/\" target=\"_blank\"><span style=\"color:#16a085\"><strong>Študijný program</strong></span></a></span></p><p style=\"margin-left:40px; text-align:justify\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><strong><a href=\"https://www.fei.stuba.sk/buxus/docs/studium_od_2023/SP_2023_2024_v2.pdf\" target=\"_blank\"><span style=\"color:#16a085\">Študijný plán</span></a></strong></span></p><p style=\"margin-left:40px; text-align:justify\"><a href=\"https://www.fei.stuba.sk/sk/uchadzacov/prijimacie-konanie-na-bakalarske-studium-na-akademicky-rok-2023-2024.html?page_id=3759#stud\" target=\"_blank\"><strong><span style=\"color:#16a085\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Informácie ku prijímaciemu konaniu</span></span></strong></a></p>'),
(4, 5, 'Hospodárska informatika', 'Áno', 'Nie', 'Slovenský', '22.4.2023', '<p style=\"margin-left:40px; text-align:justify\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">&bdquo;Absolventi študijného programu Hospodárska informatika študijného odboru Hospodárska informatika získajú komplexné poznatky z ekonomickej teórie a konkrétne skúsenosti z práce s výpočtovou technikou. Budú schopní pracovať so štandardným softvérom na automatizáciu kancelárie, podieľať sa na programovej realizácii a prevádzkovaní informačných systémov v ekonomických objektoch na všetkých stupňoch riadenia. Získajú znalosti z podnikania tak, že sa dokážu vysporiadať s agendami na zriadenie firmy ako aj jej prevádzkovanie a ďalší rozvoj. Získajú základné poznatky z hospodárskych procesov na rôznych riadiacich úrovniach. Uplatnenie absolventov je vo všetkých priemyselných odvetviach, vo verejnej správe, v súkromnej sfére, ale aj ako samostatných podnikateľov. Štúdium pripravuje odborníkov, ktorí majú dobré ekonomické vedomosti, ovládajú výpočtovú techniku a vedia uplatniť moderné informačné technológie.&ldquo; <span style=\"font-size:12px\"><a href=\"https://fhi.euba.sk/studium/bakalarske-studium/profil-absolventa\" target=\"_blank\"><span style=\"color:#2c3e50\">Zdroj</span></a></span></span></p><p style=\"margin-left:40px; text-align:justify\">&nbsp;</p><p style=\"margin-left:40px; text-align:justify\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Záujemcovia o&nbsp;absolvovanie časti&nbsp;štúdia v&nbsp;zahraničí, majú možnosť tak urobiť prostredníctvom mobilitných programov. Na výber je až z&nbsp;takmer 300 partnerských univerzít. Univerzita takisto ponúka svojim študentom možnosť získania rôznych certifikátov (ACCA, CIMA, SAS) prínosných pre budúce uplatnenie na trhu práce a&nbsp;takisto sa vyznačuje okrem iného aj výučbou viacerých cudzích jazykov v rámci štúdia všetkých študijných programov.</span></p><p style=\"margin-left:40px; text-align:justify\">&nbsp;</p><p style=\"margin-left:40px; text-align:justify\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Fakulta daný študijný program v&nbsp;externej forme štúdia momentálne neponúka. Vyučuje sa teda len v&nbsp;dennej forme, v&nbsp;slovenskom jazyku so štandardnou dĺžkou štúdia - 3 roky. Jednou z&nbsp;podmienok na prijatie je aj úspešné absolvovanie prijímacích skúšok, ktoré pozostávajú z&nbsp;testu všeobecných študijných predpokladov a&nbsp;z&nbsp;testu z&nbsp;cudzieho jazyka (ruský, anglický, taliansky, španielsky, francúzsky, nemecký) podľa výberu uchádzača. <strong><a href=\"https://euba.sk/vzorovytest/\" target=\"_blank\"><span style=\"color:#16a085\">Vzorový test</span></a></strong> &ndash; všeobecné študijné predpoklady. <strong><a href=\"https://euba.sk/uchadzac/prijimacie-konanie/vzorovy-test-anglicky-jazyk\" target=\"_blank\"><span style=\"color:#16a085\">Vzorový test</span></a></strong> &ndash; anglický jazyk.</span></p><p style=\"margin-left:40px; text-align:justify\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Viac k&nbsp;podávaniu prihlášok a&nbsp;prijímaciemu konaniu nájdeš <strong><a href=\"https://fhi.euba.sk/uchadzaci-o-studium/prijimacie-konanie-zakladne-informacie\" target=\"_blank\"><span style=\"color:#16a085\">tu</span></a></strong>.</span></p><p style=\"margin-left:40px; text-align:justify\">&nbsp;</p><p style=\"margin-left:40px; text-align:justify\"><strong><a href=\"https://fhi.euba.sk/uchadzaci-o-studium/bakalarske-studium#hospodarska-informatika\" target=\"_blank\"><span style=\"color:#16a085\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Viac o študijnom programe</span></span></a></strong></p><p style=\"margin-left:40px; text-align:justify\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><strong><a href=\"https://fhi.euba.sk/www_write/files/hi_studijny_plan.pdf\" target=\"_blank\"><span style=\"color:#16a085\">Študijný plán</span></a></strong></span></p>'),
(5, 3, 'Matematicko-počítačové modelovanie', 'Nie', 'Nie', 'Slovenský', '22.4.2023', '<p style=\"margin-left:40px; text-align:justify\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">&bdquo;Študijný program matematicko-počítačové modelovanie je koncipovaný v niekoľkých líniách. V prvom rade ide o oboznámenie sa s prostriedkami modelovania, t. j. o výborné zvládnutie tých častí matematiky, ktoré sa nevyhnutne využívajú pri modelovaní praktických problémov nielen v stavebníctve, a tiež o perfektné ovládanie programovacích jazykov a softvérov.</span></p><p style=\"margin-left:40px; text-align:justify\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Na druhej strane absolvuješ aplikačné predmety, kde sa matematické modely, počítače a softvéry používajú na simulácie a analýzu reálnych úloh.</span></p><p style=\"margin-left:40px; text-align:justify\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Naučíš sa formulovať matematické algoritmy, tvrdenia a&nbsp;definície, ale najmä to, ako ich aplikovať pri riešení mnohých praktických úloh. Programovať v&nbsp;moderných programovacích jazykoch a&nbsp;na moderných počítačových architektúrach. Ovládať mnohé užitočné softvéry na modelovanie a&nbsp;simuláciu technických a&nbsp;ekonomických úloh z&nbsp;praxe (ANSYS, MatLab, Mathematica, ParaView, R, AutoCad). Používať všetky dôležité metódy modernej aplikovanej matematiky, predovšetkým numerické, štatistické a&nbsp;optimalizačné metódy riešenia praktických úloh spolu s&nbsp;ich efektívnou počítačovou implementáciou. &ldquo; <span style=\"font-size:12px\"><a href=\"https://www.stavebnarevolucia.sk/matematicko-pocitacove-modelovanie/\" target=\"_blank\"><span style=\"color:#2c3e50\">Zdroj</span></a></span></span></p><p style=\"margin-left:40px; text-align:justify\">&nbsp;</p><p style=\"margin-left:40px; text-align:justify\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Časť štúdia je možné absolvovať aj v&nbsp;zahraničí. Na výber je z&nbsp;takmer 30 krajín Európy, a&nbsp;teda 75 prestížnych univerzít, s&nbsp;ktorými SvF spolupracuje.</span></p><p style=\"margin-left:40px; text-align:justify\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Pre záujemcov o&nbsp;ďalšie štúdium, na bakalársky študijný program Matematicko-počítačové modelovanie nadväzuje rovnomenný inžiniersky program.</span></p><p style=\"margin-left:40px; text-align:justify\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Fakulta daný študijný program v&nbsp;externej forme štúdia momentálne neponúka. Vyučuje sa teda len v&nbsp;dennej forme, v&nbsp;slovenskom jazyku so štandardnou dĺžkou štúdia - 3 roky. Prijímacie skúšky v&nbsp;danom akademickom roku na študijný program Matematicko-počítačové modelovanie neboli stanovené. Viac k&nbsp;prijímaciemu konaniu, prihláškam a poplatkom nájdeš <strong><a href=\"https://www.svf.stuba.sk/sk/pre-uchadzacov/bakalarske-studium.html?page_id=1702\" target=\"_blank\"><span style=\"color:#16a085\">tu</span></a></strong>.</span></p><p style=\"margin-left:40px; text-align:justify\">&nbsp;</p><p style=\"margin-left:40px; text-align:center\"><iframe allow=\"accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture; web-share\" allowfullscreen=\"\" frameborder=\"0\" height=\"315\" src=\"https://www.youtube-nocookie.com/embed/v-buBEIw4dk\" title=\"YouTube video player\" width=\"560\"></iframe></p><p style=\"margin-left:40px; text-align:justify\">&nbsp;</p><p style=\"margin-left:40px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><strong><a href=\"https://www.stavebnarevolucia.sk/matematicko-pocitacove-modelovanie/\" target=\"_blank\"><span style=\"color:#16a085\">Študijný program</span></a></strong></span></p>'),
(6, 8, 'Komunikačné a informačné technológie', 'Áno', 'Nie', 'Slovenský', '22.4.2023', '<p style=\"margin-left:40px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">&bdquo;Absolvent bakalárskeho študijného programu komunikačné a&nbsp;informačné technológie v&nbsp;študijnom odbore Informatika získa schopnosť špecializovať a&nbsp;adaptovať sa podľa najnovších a&nbsp;budúcich potrieb a&nbsp;požiadaviek praxe vo výrobných podnikoch u prevádzkovateľov sietí a&nbsp;v&nbsp;podnikoch služieb v&nbsp;oblasti elektronických komunikácií. Bude pripravený na trvalé prehlbovanie vedomostí z&nbsp;odboru. Uplatní sa ako kvalifikovaný pracovník pre prevádzku a&nbsp;projektovanie technológie komunikačných sietí a&nbsp;služieb, schopný v&nbsp;tíme riešiť úlohy praxe. Hlavné uplatnenie absolventov je na pozíciách výkonných pracovníkov, operátorov elektronických komunikácií, sieťových špecialistov v&nbsp;podnikoch a&nbsp;inštitúciách a&nbsp;vývojových pracovníkov komunikačných služieb a&nbsp;v&nbsp;podnikoch rozvíjajúcich technológie Priemyslu 4.0. Absolvent sa uplatní ako projektant, konštruktér, systémový návrhár, či ako špecialista pre rôzne oblasti IKT.&ldquo; <span style=\"font-size:12px\"><a href=\"https://feit.uniza.sk/ponukane-studijne-programy-bakalarske-studium/\" target=\"_blank\"><span style=\"color:#4e5f70\">Zdroj</span></a></span></span></p><p style=\"margin-left:40px\">&nbsp;</p><p style=\"margin-left:40px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Jedná sa o&nbsp;verejnú školu, a&nbsp;teda bez nutnosti platiť školné. Dĺžka štúdia tohto študijného programu je priemerná - 3 roky dennou formou. Podmienkami na prijatie na bakalárske štúdium zvoleného študijného programu je &nbsp;znalosť slovenského, alebo českého jazyka minimálne na úrovni A2, získanie úplného stredného všeobecného/odborného vzdelania a&nbsp;takisto úspešné absolvovanie <strong><a href=\"http://feit.uniza.sk/podmienky-prijatia-bakalarske-studium/\" target=\"_blank\"><span style=\"color:#16a085\">prijímacích skúšok</span></a></strong>. Možnosť prijatia bez prijímacej skúšky je možná, na základe univerzitou stanovených pravidiel v&nbsp;súvislosti s&nbsp;olympiádami resp. súťažami v&nbsp;matematike, fyzike, informatike a&nbsp;iných.</span></p><p style=\"margin-left:40px\">&nbsp;</p><p style=\"margin-left:40px; text-align:center\"><iframe allow=\"accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture; web-share\" allowfullscreen=\"\" frameborder=\"0\" height=\"315\" src=\"https://www.youtube-nocookie.com/embed/i7Ug3sRRk_A\" title=\"YouTube video player\" width=\"560\"></iframe></p><p style=\"margin-left:40px\">&nbsp;</p><p style=\"margin-left:40px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><strong><a href=\"https://www.feitcity.sk/co-studovat-na-feit/komunikacne-informacne-technologie/\" target=\"_blank\"><span style=\"color:#16a085\">Viac o študijnom programe</span></a></strong></span></p>'),
(7, 9, 'Informatika', 'Áno', 'Nie', 'Slovenský', '22.4.2023', '<p style=\"margin-left:40px; text-align:justify\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">&bdquo;Najvyhľadávanejší absolventi na trhu práce &ndash;&nbsp;informatici.&nbsp;Zastarané vnímanie tohto odboru je už dávno za nami. Vieme, že práve informatika tvorí všetky možnosti pre komunikáciu, efektívnejšiu prácu a posúva naše schopnosti vpred. Hľadanie nových riešení, kreatívne myslenie a schopnosť ukázať viac. To a oveľa viac dostaneš na FRI v študijnom programe informatika.</span></p><p style=\"margin-left:40px; text-align:justify\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Absolventi študijného programu&nbsp;Informatika&nbsp;získajú základné poznatky z informatiky. Budú schopní pracovať so softvérom informačných systémov podnikov, podieľať sa na jeho tvorbe a realizácii, získajú znalosti z podnikania. Typické uplatnenie absolventov je vo všetkých priemyselných odvetviach, vo verejnej správe, v súkromnej sfére a ako samostatní podnikatelia. Štúdium pripravuje odborníkov, ktorí ovládajú výpočtovú techniku a vedia uplatniť moderné informačné technológie, vytvára predpoklady pre ďalší kvalifikačný rast v oblasti informatiky v rámci inžinierskeho štúdia.&ldquo; <span style=\"font-size:12px\"><a href=\"https://www.fri.uniza.sk/stranka/predstavenie-studijnych-programov\" target=\"_blank\"><span style=\"color:#2c3e50\">Zdroj</span></a></span></span></p><p style=\"margin-left:40px; text-align:justify\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Jedná sa o&nbsp;verejnú školu, a&nbsp;teda bez nutnosti platiť školné. Dĺžka štúdia tohto študijného programu je priemerná - 3 roky dennou formou. Univerzita daný študijný program formou externého štúdia neponúka. Prijímacia skúška pozostáva z&nbsp;testu znalostí zo stredoškolskej matematiky, ale aj testu schopností všeobecného logického myslenia. Študent môže byť prijatý na štúdium aj bez prijímacej skúšky. (Viac <strong><a href=\"https://www.fri.uniza.sk/uploads/files/1634110780-FRI-BC-2022.pdf\" target=\"_blank\"><span style=\"color:#16a085\">info</span></a></strong> : strana 2 &ndash; &bdquo;PODMIENKY PRIJATIA&ldquo;).</span></p><p style=\"margin-left:40px; text-align:justify\">&nbsp;</p><p style=\"margin-left:40px; text-align:justify\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><a href=\"https://www.fri.uniza.sk/stranka/ponukane-bakalarske-studijne-programy\" target=\"_blank\"><span style=\"color:#16a085\"><strong>Študijný program</strong></span></a></span></p><p style=\"margin-left:40px; text-align:justify\"><a href=\"https://www.youtube.com/channel/UCEWpHpMHALqhBrKP21oyxxA/videos\" target=\"_blank\"><span style=\"color:#16a085; font-family:Tahoma,Geneva,sans-serif\"><strong>Youtube kanál fakulty</strong></span></a></p>'),
(8, 9, 'Informatika a riadenie', 'Áno', 'Nie', 'Slovenský', '22.4.2023', '<p style=\"margin-left:40px; text-align:justify\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">&bdquo;Študijný program&nbsp;informatika a riadenie&nbsp;v sebe vhodne kombinuje znalosti z oboch oblastí. Jeho výhodou je možnosť špecializácie po prvom roku štúdia. Na základe tvojich preferencií sa môžeš rozhodnúť venovať viac informatike alebo viac oblasti riadenia podnikov s využitím moderných informačných technológií. Staň sa s nami úspešným IT špecialistom, programátorom, IT manažérom, vývojárom mobilných aplikácií, testerom, vedúcim projektových tímov, správcom počítačových sietí, analytikom informačných systémov, tím lídrom, programátorom webových aplikácií, či IT konzultantom.&ldquo; <span style=\"font-size:12px\"><a href=\"https://www.fri.uniza.sk/stranka/predstavenie-studijnych-programov\" target=\"_blank\"><span style=\"color:#2c3e50\">Zdroj</span></a></span></span></p><p style=\"margin-left:40px; text-align:justify\">&nbsp;</p><p style=\"margin-left:40px; text-align:justify\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Jedná sa o&nbsp;štátnu školu, a&nbsp;teda bez nutnosti platiť školné. Dĺžka štúdia tohto študijného programu je priemerná - 3 roky dennou formou. Univerzita daný študijný program formou externého štúdia neponúka. Prijímacia skúška pozostáva z&nbsp;testu znalostí zo stredoškolskej matematiky, ale aj testu schopností všeobecného logického myslenia. Študent môže byť prijatý na štúdium aj bez prijímacej skúšky. Viac info <strong><a href=\"https://www.fri.uniza.sk/uploads/files/1634110780-FRI-BC-2022.pdf\" target=\"_blank\"><span style=\"color:#16a085\">tu</span></a></strong></span>&nbsp;<span style=\"font-family:Tahoma,Geneva,sans-serif\">:&nbsp;strana 2 &ndash; &bdquo;PODMIENKY PRIJATIA&ldquo;.</span></p><p style=\"margin-left:40px; text-align:justify\">&nbsp;</p><p style=\"margin-left:40px; text-align:justify\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><strong><a href=\"https://www.fri.uniza.sk/stranka/ponukane-bakalarske-studijne-programy\" target=\"_blank\"><span style=\"color:#16a085\">Študijný program</span></a></strong></span></p><p style=\"margin-left:40px; text-align:justify\"><a href=\"http://www.youtube.com/channel/UCEWpHpMHALqhBrKP21oyxxA/videos\" target=\"_blank\"><span style=\"color:#16a085; font-family:Tahoma, Geneva, sans-serif\"><strong>Youtube kanál fakulty</strong></span></a></p>'),
(9, 9, 'Informačné a sieťové technológie', 'Áno', 'Nie', 'Slovenský', '22.4.2023', '<p style=\"margin-left:40px; text-align:justify\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">&bdquo;Získaj poznatky z oblasti architektúr počítačových systémov, obvodových riešení, pevných a bezdrôtových sietí, bezpečnosti a zabezpečenia počítačovej komunikácie, princípov a správy operačných systémov a riešení virtualizácie, všeobecných aj špeciálnych techník programovania. Ovládaj nielen aktuálne technológie, ale buď pripravený rýchlo sa adaptovať na novovznikajúce technológie. Výhodou tohto študijného programu je možnosť profilovať sa do jedného z dvoch ponúkaných zameraní:&nbsp;sieťové technológie&nbsp;alebo&nbsp;informačné technológie.&ldquo; <span style=\"font-size:12px\"><a href=\"https://www.fri.uniza.sk/stranka/predstavenie-studijnych-programov\" target=\"_blank\"><span style=\"color:#2c3e50\">Zdroj</span></a></span></span></p><p style=\"margin-left:40px; text-align:justify\">&nbsp;</p><p style=\"margin-left:40px; text-align:justify\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Jedná sa o verejnú školu, a&nbsp;teda bez nutnosti platiť školné. Dĺžka štúdia tohto študijného programu je priemerná - 3 roky dennou formou. Univerzita daný študijný program formou externého štúdia neponúka. Prijímacia skúška pozostáva z&nbsp;testu znalostí zo stredoškolskej matematiky, ale aj testu schopností všeobecného logického myslenia. Študent môže byť prijatý na štúdium aj bez prijímacej skúšky. (Viac info <strong><a href=\"https://www.fri.uniza.sk/uploads/files/1634110780-FRI-BC-2022.pdf\" target=\"_blank\"><span style=\"color:#16a085\">tu</span></a></strong> :&nbsp;strana 2 &ndash; &bdquo;PODMIENKY PRIJATIA&ldquo;).</span></p><p style=\"margin-left:40px; text-align:justify\">&nbsp;</p><p style=\"margin-left:40px; text-align:justify\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><strong><a href=\"https://www.fri.uniza.sk/stranka/ponukane-bakalarske-studijne-programy\" target=\"_blank\"><span style=\"color:#16a085\">Študijný program</span></a></strong></span></p><p style=\"margin-left:40px; text-align:justify\"><a href=\"https://www.youtube.com/channel/UCEWpHpMHALqhBrKP21oyxxA/videos\" target=\"_blank\"><span style=\"color:#16a085; font-family:Tahoma,Geneva,sans-serif\"><strong>Youtube kanál fakulty</strong></span></a></p>'),
(10, 9, 'Počítačové inžinierstvo', 'Áno', 'Nie', 'Slovenský', '22.4.2023', '<p style=\"margin-left:40px; text-align:justify\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">&bdquo;Byť ten, ktorý vie odpovedať na všetky technické otázky, vedieť sa vynájsť a&nbsp;mať chuť hľadať nové riešenia a vylepšenia, to je skrátka&nbsp;študent PI. Každý jeden študent PI je považovaný za frajera, ktorý vždy vie, ako veci fungujú. Už po prvom stupni štúdia počítačového inžinierstva vychádzajú z FRI kvalifikovaní odborníci, ktorých nič len tak nezaskočí. Ak Ti pojmy ako operačný systém, operačný zosilňovač či mikrokontrolér alebo zbernica nie sú cudzie, prípadne sa chceš o nich dozvedieť viac, patríš rozhodne do nášho tímu.</span></p><p style=\"margin-left:40px; text-align:justify\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Absolvent študijného programu&nbsp;Počítačové inžinierstvo&nbsp;je pripravený pokračovať v inžinierskom štúdiu, alebo sa uplatniť v podnikoch a inštitúciách pri projektovaní, nasadzovaní, prevádzke, údržbe a inovácii počítačových systémov, prostriedkov komunikačnej techniky, priemyselnej automatizácie, meracej a diagnostickej techniky a podobne. Uplatní sa pri vývoji číslicových systémov na báze mikropočítačov a programovateľných obvodov, čo mu umožňuje pracovať tiež na pozícii vývojového pracovníka, konštruktéra, prípadne technológa.&ldquo; <span style=\"font-size:12px\"><a href=\"https://www.fri.uniza.sk/stranka/predstavenie-studijnych-programov\" target=\"_blank\"><span style=\"color:#2c3e50\">Zdroj</span></a></span></span></p><p style=\"margin-left:40px; text-align:justify\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Jedná sa o&nbsp;verejnú školu, a&nbsp;teda bez nutnosti platiť školné. Dĺžka štúdia tohto študijného programu je priemerná - 3 roky dennou formou. Univerzita daný študijný program formou externého štúdia neponúka. Prijímacia skúška pozostáva z&nbsp;testu znalostí zo stredoškolskej matematiky, ale aj testu schopností všeobecného logického myslenia. Študent môže byť prijatý na štúdium aj bez prijímacej skúšky. (Viac info <strong><a href=\"https://www.fri.uniza.sk/uploads/files/1634110780-FRI-BC-2022.pdf\" target=\"_blank\"><span style=\"color:#16a085\">tu</span></a></strong> :&nbsp;strana 2 &ndash; &bdquo;PODMIENKY PRIJATIA&ldquo;).</span></p><p style=\"margin-left:40px; text-align:justify\">&nbsp;</p><p style=\"margin-left:40px; text-align:justify\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><strong><a href=\"https://www.fri.uniza.sk/stranka/ponukane-bakalarske-studijne-programy\" target=\"_blank\"><span style=\"color:#16a085\">Študijný program</span></a></strong></span></p><p style=\"margin-left:40px; text-align:justify\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><strong><a href=\"https://www.youtube.com/channel/UCEWpHpMHALqhBrKP21oyxxA/videos\" target=\"_blank\"><span style=\"color:#16a085\">Youtube kanál fakulty</span></a></strong></span></p>'),
(11, 15, 'Aplikovaná informatika', 'Nie', 'Nie', 'Maďarský', '22.4.2023', '<p style=\"margin-left:40px; text-align:justify\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">&quot;Absolvent získa potrebné teoretické vedomosti a praktické skúsenosti z tvorby sieťových a webových aplikácií, z tvorby, obsluhy a údržby centralizovaných a distribuovaných elektronických informačných a administratívnych systémov, počítačových sietí, má schopnosti využiť teoretické poznatky a príslušné nástroje pri špecifikácii, navrhovaní, implementácii a údržbe počítačových systémov a počítačových sietí.</span></p><p style=\"margin-left:40px; text-align:justify\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Absolvent bude schopný analyzovať, implementovať, modifikovať a modernizovať počítačové a informačné systémy. Bude schopný nachádzať vlastné originálne riešenia problémov pri navrhovaní a využívaní informačných systémov. Absolvent získa vnútornú potrebu permanentného vzdelávania, dokáže sledovať vývoj počítačových systémov a informačných technológií, aktualizovať svoje vedomosti a rozvíjať svoje zručnosti. Bude schopný použiť princípy efektívnej práce s informáciami rôzneho druhu a z rôznych zdrojov vrátane Internetu, prevádzkovať počítačové a softvérové systémy účinne a prezentovať technické problémy a ich riešenia. Zároveň je pripravený získať vzdelanie druhého stupňa vysokoškolského štúdia vo svojom odbore.</span></p><p style=\"margin-left:40px; text-align:justify\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Absolvent bude aktívne ovládať aspoň jeden svetový jazyk a ďalší jazyk pasívne. Bude pripravený samostatne aj v tíme riešiť zložité projekty a prezentovať svoje výsledky, pracovať efektívne ako člen vývojového tímu, bude schopný organizovať a manažovať prácu tímu, vyvíjať vlastné podnikateľské aktivity, prípadne si založiť aj vlastnú firmu. Uplatní sa ako manažér, analytik, systémový návrhár a špecialista v bankovníctve, poisťovníctve, konzultačných firmách, v priemysle a na školách. Nájde uplatnenie pri návrhu a aplikácii informačných systémov vo všetkých druhoch podnikov, v organizáciách a službách, ako napríklad v oblasti bankovníctva, administratívy, dopravy a zdravotníctva pri implementácii, inovácii a údržbe technických prostriedkov a ich programového vybavenia, v softvérových firmách orientovaných na tvorbu rôznych softvérových aplikácií, v oblasti školstva ako technický pracovník alebo správca informačných systémov a počítačových sietí.&quot;&nbsp;</span><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"font-size:12px\"><a href=\"https://www.ujs.sk/sk/ponukane-studijne-programy/ekonomicka-fakulta.html\" target=\"_blank\"><span style=\"color:#2c3e50\">Zdroj</span></a></span></span></p><p style=\"margin-left:40px; text-align:justify\">&nbsp;</p><p style=\"margin-left:40px; text-align:justify\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Fakulta ekonómie a&nbsp;informatiky UJS je jedinou fakultou na Slovensku, ktorá umožňuje študentom získať vysokoškolské vzdelanie v&nbsp;jazyku maďarskej národnostnej menšiny v&nbsp;oblasti ekonómie a&nbsp;informatiky. Navyše, UJS je verejnou školou, a&nbsp;teda nevzniká povinnosť pre študenta platiť školné, ak sa rozhodne študovať dennou formou.&nbsp;Študijný odbor Aplikovaná informatika sa však v&nbsp;súčasnosti pre externistov univerzitou neponúka. Dĺžka štúdia pre prezenčnú formu predstavuje 3 roky. Prijímacie konanie sa realizuje bez prijímacej skúšky.</span></p><p style=\"margin-left:47px; text-align:justify\">&nbsp;</p><p style=\"margin-left:47px; text-align:center\"><iframe allow=\"accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture; web-share\" allowfullscreen=\"\" frameborder=\"0\" height=\"315\" src=\"https://www.youtube-nocookie.com/embed/j-YqH2OadVM\" title=\"YouTube video player\" width=\"560\"></iframe></p><p style=\"margin-left:47px; text-align:justify\">&nbsp;</p><p style=\"margin-left:47px; text-align:justify\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><strong><a href=\"https://www.ujs.sk/sk/ponukane-studijne-programy/ekonomicka-fakulta.html\" target=\"_blank\"><span style=\"color:#16a085\">Študijný program</span></a></strong></span></p><p style=\"margin-left:47px; text-align:justify\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><strong><a href=\"https://ais2.ujs.sk/repovsk/repository/default/ais/vsk/Fakulta_ekonomie_a_informatiky/informatika/studijny_program/Aplikovana_informatika_(I._st._denna_forma)_(AIdb22)/Studijnyplan/Studijny_plan_(SK).html\" target=\"_blank\"><span style=\"color:#16a085\">Študijný plán</span></a></strong></span></p><p style=\"margin-left:47px; text-align:justify\">&nbsp;</p>'),
(12, 13, 'Aplikovaná informatika a vývoj softvéru', 'Nie', 'Áno', 'Slovenský', '22.4.2023', '<p style=\"margin-left:40px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">&bdquo;Aplikovaná informatika a vývoj softvéru (Bc.) ‒ Absolventi študijného programu 1. stupňa profesijne orientovaného vysokoškolského štúdia aplikovaná informatika a vývoj softvéru dokážu analyzovať, navrhovať, implementovať, udržiavať a testovať softvérové systémy informačných technológií tvorivo a samostatne. Dôraz je kladený na to, aby absolvent študijného programu získal vedomosti a praktické skúsenosti v oblasti vývoja softvérových systémov, umožňujúce mu využiť ich v priemysle, ekonomike, vzdelávaní, zdravotníctve a ďalších odvetviach. Absolvent je schopný uplatniť sa po ukončení štúdia v praxi, ale aj úspešne pokračovať v štúdiu aplikovanej informatiky na 2. stupni.&ldquo; <span style=\"font-size:12px\"><a href=\"https://www.umb.sk/app/cmsFile.php?disposition=i&amp;ID=6705\" target=\"_blank\"><span style=\"color:#2c3e50\">Zdroj</span></a></span><span style=\"color:#2c3e50\">&nbsp;</span></span></p><p style=\"margin-left:40px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Univerzita Mateja Bela v&nbsp;Banskej Bystrici je verejnou školou, a&nbsp;teda nevzniká povinnosť pre študenta platiť školné, ak sa rozhodne študovať dennou formou. Poplatky za školné sa spájajú s&nbsp;externým štúdiom, prekročením štandardnej dĺžky štúdia, štúdiom v&nbsp;inom, ako slovenskom jazyku atp. Prijímacie konanie na FPV sa realizuje bez prijímacej skúšky v&nbsp;prípade, ak počet záujemcov o&nbsp;štúdium nepresiahne kapacitné kvóty. V&nbsp;opačnom prípade, bude realizovaná písomná prijímacia skúška. Viac k&nbsp;prijímaciemu konaniu nájdete <strong><a href=\"https://www.fpv.umb.sk/katedry/katedra-informatiky/pre-uchadzacov/informacie-o-podmienkach-prijatia-na-studium/aplikovana-informatika-bc-6824.html\" target=\"_blank\"><span style=\"color:#16a085\">tu</span></a></strong>.</span></p><p style=\"margin-left:40px\">&nbsp;</p><p style=\"margin-left:40px; text-align:center\"><iframe allow=\"accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture; web-share\" allowfullscreen=\"\" frameborder=\"0\" height=\"315\" src=\"https://www.youtube-nocookie.com/embed/Uj4-EWVum-U\" title=\"YouTube video player\" width=\"560\"></iframe></p><p style=\"margin-left:40px\">&nbsp;</p><p style=\"margin-left:40px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><strong><a href=\"https://www.fpv.umb.sk/app/cmsFile.php?disposition=i&amp;ID=21272\" target=\"_blank\"><span style=\"color:#16a085\">Študijný program</span></a></strong></span></p><p style=\"margin-left:40px\"><a href=\"https://www.fpv.umb.sk/app/cmsFile.php?disposition=i&amp;ID=21489\" target=\"_blank\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><strong><span style=\"color:#16a085\">Študijný plán</span></strong></span></a></p><p style=\"margin-left:40px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><strong><a href=\"https://www.fpv.umb.sk/katedry/katedra-informatiky/pre-uchadzacov/\" target=\"_blank\"><span style=\"color:#16a085\">Viac info pre uchádzačov</span></a></strong></span></p>'),
(13, 11, 'Aplikovaná informatika', 'Nie', 'Áno', 'Slovenský', '15.4.2023', '<p style=\"margin-left:40px; text-align:justify\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">&bdquo;Študijný program aplikovaná informatika je súčasťou študijného odboru 18 informatika. Zameriava sa na princípy, metódy a nástroje pre tvorbu softvérových aplikácií, technológie vývoja softvérových aplikácií, grafické spracovanie údajov a vizualizáciu údajov v prírodných vedách. Študijný program je navrhnutý tak, aby jeho absolvent bol schopný po jeho úspešnom ukončení nastúpiť do zamestnania a realizovať sa podľa vlastných predstáv a možností trhu práce.&ldquo; <span style=\"font-size:12px\"><a href=\"https://kai.fpv.ucm.sk/aplikovana_informatika.php\" target=\"_blank\"><span style=\"color:#2c3e50\">Zdroj</span></a></span></span></p><p style=\"margin-left:40px; text-align:justify\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Univerzita sv. Cyrila a&nbsp;Metoda v&nbsp;Trnave je verejnou školou, a&nbsp;teda bez nutnosti platiť školné, to sa však nevzťahuje na externé štúdium, ktoré je spoplatnené. Dĺžka štúdia tohto študijného programu je priemerná - 3 roky dennou formou a&nbsp;4 externou formou. Prijímacie konanie prebieha bez prijímacej skúšky.</span></p><p style=\"margin-left:40px; text-align:justify\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">V&nbsp;danom študijnom odbore sa vyučujú predmety ako: počítačové siete, matematické základy informatiky, počítačová grafika, databázové systémy, elektrotechnika a&nbsp;elektronika a&nbsp;mnoho ďalších. Viac k&nbsp;učebnému plánu <strong><a href=\"http://fpv.ucm.sk/images/ucebne_plany_infolisty/APIN_BcD19.pdf\" target=\"_blank\"><span style=\"color:#16a085\">tu</span></a></strong>.&nbsp;</span></p><p style=\"margin-left:40px; text-align:justify\">&nbsp;</p><p style=\"margin-left:40px; text-align:justify\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><strong><a href=\"https://kai.fpv.ucm.sk/aplikovana_informatika.php\" target=\"_blank\"><span style=\"color:#16a085\">Študijný program</span></a></strong></span></p><p style=\"margin-left:40px; text-align:justify\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><strong><a href=\"http://fpv.ucm.sk/images/prijimacie_konanie/Prijimacie%20konanie_AR_2023_2024_upraven.pdf\" target=\"_blank\"><span style=\"color:#16a085\">Viac info pre uchádzačov</span></a></strong></span></p>'),
(14, 12, 'Aplikovaná informatika', 'Nie', 'Nie', 'Slovenský', '15.4.2023', '<p style=\"margin-left:40px; text-align:justify\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">&bdquo;Študijný program aplikovaná informatika pripravuje vysokokvalifikovaných špecialistov s okamžitým uplatnením sa nielen vo všetkých oblastiach IT, ale prakticky všade, kde je vyžadované koncepčné myslenie a IT zručnosti.</span></p><p style=\"margin-left:40px; text-align:justify\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Na rozdiel od podobných študijných programov na iných univerzitách ponúka náš študijný program širší záber &ndash; obsahuje moduly typické pre prírodovedné a súčasne aj pre technické odbory a umožňuje tak študentovi zamerať sa buď úzkoprofilovo alebo širokospektrálne už na bakalárskom stupni.</span></p><p style=\"margin-left:40px; text-align:justify\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Absolvent je schopný začleniť sa do pracovného procesu prakticky okamžite, pretože technológie používané vo výučbe zodpovedajú tým, ktoré sú nasadené v praxi. Bežný absolvent dokáže programovať a navrhovať logiku i dizajn všetkých typov aplikácií, vytvoriť alebo implementovať matematický model, umelú inteligenciu alebo jednoduchého robota či navrhnúť, poskladať a naprogramovať hardvérové riešenie na sledovanie domácnosti.&ldquo; <span style=\"font-size:12px\"><a href=\"https://www.fpvai.ukf.sk/sk/studium-fpv-ukf/aktualna-ponuka-studijnych-programov/139-studium/aktualna-ponuka-studijnych-programov/bakalarske-studijne-programy/952-aplikovana-informatika\" target=\"_blank\"><span style=\"color:#2c3e50\">Zdroj</span></a></span></span></p><p style=\"margin-left:40px; text-align:justify\">&nbsp;</p><p style=\"margin-left:40px; text-align:justify\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Univerzita Konštantína Filozofa v&nbsp;Nitre je verejnou školou, a&nbsp;teda bez nutnosti platiť školné (vzťahuje sa len na denné štúdium). Študijný program Aplikovaná informatika v&nbsp;akademickom roku 2023/2024 je ponúkaný len v dennej forme so štandardnou dĺžkou štúdia 3 roky. Na štúdium sú prijatí uchádzači, ktorí preukážu najvyššiu mieru schopností a predpokladov na štúdium. Základným kritériom hodnotenia sú výsledky predchádzajúceho štúdia, a&nbsp;teda prijímacie konanie prebieha bez prijímacej skúšky.&nbsp;</span></p><p style=\"margin-left:40px; text-align:justify\">&nbsp;</p><p style=\"margin-left:40px; text-align:justify\"><strong><a href=\"https://www.fpvai.ukf.sk/sk/studium-fpv-ukf/aktualna-ponuka-studijnych-programov/139-studium/aktualna-ponuka-studijnych-programov/bakalarske-studijne-programy/952-aplikovana-informatika\" target=\"_blank\"><span style=\"color:#16a085\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Študijný program</span></span></a></strong></p><p style=\"margin-left:40px; text-align:justify\"><strong><a href=\"https://www.fpvai.ukf.sk/images/Propagacia/Studijne_plany/informatika/AI15b.pdf\" target=\"_blank\"><span style=\"color:#16a085\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Študijný plán</span></span></a></strong></p>');
INSERT INTO `studijny_program` (`id`, `fakulta_ID`, `nazov`, `prijimacie_skusky`, `ponuka_externeho_studia`, `vyucovaci_jazyk`, `datum_aktualizacie_clanku`, `clanok`) VALUES
(15, 4, 'Aplikovaná informatika a automatizácia v priemysle', 'Nie', 'Nie', 'Slovenský', '22.4.2023', '<p style=\"margin-left:40px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Študijný program Aplikovaná informatika a&nbsp;automatizácia v&nbsp;priemysle v&nbsp;sebe spája rovno dve odvetvia a&nbsp;to pohľad&nbsp; informatika a&nbsp;automatizéra. Naučíš sa programovať v&nbsp;jazykoch C, Visual C, C#, vytvárať databázy a navrhovať aplikácie pre podniky a&nbsp;firmy. Takisto sa naučíš princípu počítačových sietí, programovať PLC, ktoré sa používajú v automatizovaných linkách, ovládať technické prostriedky automatizovaného riadenia, využívať softvér Matlab na analýzu, modelovanie, návrh algoritmov a&nbsp;simuláciu a&nbsp;mnoho ďalšieho, čo sa ti v&nbsp;praxi zaručene zíde. <span style=\"font-size:12px\"><a href=\"https://www.mtf.stuba.sk/sk/uchadzacov/prijimacie-konanie-na-bakalarsky-stupen-studia/aplikovana-informatika-a-automatizacia-v-priemysle.html?page_id=14062\" target=\"_blank\"><span style=\"color:#2c3e50\">Zdroj</span></a></span></span></p><p style=\"margin-left:40px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">STU je verejnou školou, a&nbsp;teda nevzniká povinnosť pre študenta platiť školné, ak sa rozhodne študovať dennou formou. Externá forma štúdia sa momentálne pre daný študijný program neponúka. Alternatívou však môže byť pre študenta štúdium v kombinovanej metóde, čo znamená, že výučba prebieha v piatok a sobotu. Na rozdiel od klasického externého štúdia, tento typ štúdia je bezplatný. Viac ku možnostiam spôsobu výučby <u><strong><a href=\"https://www.mtf.stuba.sk/sk/casto-kladene-otazky-faq/formy-a-metody-studia-na-mtf-stu.html?page_id=2678\" target=\"_blank\"><span style=\"color:#16a085\">tu</span></a></strong></u>. Prijatie na štúdium programu&nbsp;Aplikovaná informatika a&nbsp;automatizácia nie je podmienený prijímacou skúškou, a teda nie je.</span></p><p style=\"margin-left:40px\">&nbsp;</p><p style=\"margin-left:40px; text-align:center\"><iframe allow=\"accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture; web-share\" allowfullscreen=\"\" frameborder=\"0\" height=\"315\" src=\"https://www.youtube-nocookie.com/embed/E14H0OlL8-I\" title=\"YouTube video player\" width=\"560\"></iframe></p><p style=\"margin-left:40px; text-align:center\">&nbsp;</p><p style=\"margin-left:40px\"><strong><a href=\"https://www.mtf.stuba.sk/sk/uchadzacov/prijimacie-konanie-na-bakalarsky-stupen-studia/aplikovana-informatika-a-automatizacia-v-priemysle.html?page_id=14062\" target=\"_blank\"><span style=\"color:#16a085\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Študijný program</span></span></a></strong></p><p style=\"margin-left:40px\"><strong><a href=\"https://www.mtf.stuba.sk/buxus/docs/doc/Informacie_pre_studentov/2022/Studijne_plany_-_bakalarske_studium_2022-23_aktualizacia_11.7.2022.pdf\" target=\"_blank\"><span style=\"color:#16a085\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Študijný plán</span></span></a></strong></p>'),
(16, 14, 'Informatika', 'Nie', 'Nie', 'Slovenský', '18.4.2023', '<p style=\"margin-left:40px; text-align:justify\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Študijný program Informatika na UPJŠ v&nbsp;Košiciach ťa naučí pretaviť vlastný nápad do reálnej počítačovej aplikácie, princípy autonómnej navigácie, určiť slabé miesta IT systémov a&nbsp;zabrániť kyberútokom, riadiť tím programátorov, budovať umelú inteligenciu, analyzovať obrovské dáta a&nbsp;všetko potrebné na prácu v&nbsp;IT sektore. <span style=\"font-size:12px\"><a href=\"https://www.upjs.sk/prirodovedecka-fakulta/bakalarske-studium-pk/\" target=\"_blank\"><span style=\"color:#2c3e50\">Zdroj</span></a></span></span></p><p style=\"margin-left:40px; text-align:justify\">&nbsp;</p><p style=\"margin-left:40px; text-align:justify\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Prijímacie konanie je realizované bez prijímacej skúšky na základe dosiahnutých študijných výsledkov z&nbsp;predchádzajúceho štúdia resp. strednej školy. Automatické prijatie na štúdium na UPJŠ je garantované pre uchádzačov, ktorých študijný priemer z&nbsp;polročného vysvedčenia v&nbsp;poslednom roku štúdia a&nbsp;z&nbsp;koncoročných vysvedčení v&nbsp;predchádzajúcich troch rokoch štúdia nepresiahol stanovený študijný priemer (2,00). V&nbsp;opačnom prípade je uchádzať hodnotený podľa výsledkov z&nbsp;maturitnej skúšky z&nbsp;predmetov Matematika a/alebo Informatika. Akceptovateľné sú takisto výsledky SCIO testov. Viac k&nbsp;podmienkam na prijatie <strong><a href=\"https://www.upjs.sk/public/media/2453/prij_konanie_PFUPJS_22_23_Bc.pdf\" target=\"_blank\"><span style=\"color:#16a085\">tu</span></a></strong> a <strong><a href=\"https://studijne-programy.upjs.sk/prihlaska/Ib?prihlasky=\" target=\"_blank\"><span style=\"color:#16a085\">tu</span></a></strong>.</span></p><p style=\"margin-left:40px; text-align:justify\">&nbsp;</p><p style=\"margin-left:40px; text-align:justify\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">UPJŠ je verejnou vysokou školou, a&nbsp;teda nevzniká povinnosť pre študenta platiť školné, ak sa rozhodne študovať dennou formou. Externé štúdium fakulta neponúka. Štandardná dĺžka štúdia je 3 roky.</span></p><p style=\"margin-left:40px; text-align:justify\">&nbsp;</p><p style=\"margin-left:40px; text-align:justify\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><strong><a href=\"https://www.upjs.sk/prirodovedecka-fakulta/informacie-pre-zaujemcov-o-studium/studium/informatika-bakalarsky-informatika/\" target=\"_blank\"><span style=\"color:#16a085\">Študijný program</span></a></strong></span></p><p style=\"margin-left:40px; text-align:justify\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><strong><a href=\"https://studijne-programy.upjs.sk/program/Ib\" target=\"_blank\"><span style=\"color:#16a085\">Študijný plán</span></a></strong></span></p>'),
(17, 14, 'Aplikovaná informatika', 'Nie', 'Nie', 'Slovenský', '18.4.2023', '<p style=\"margin-left:40px; text-align:justify\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">&bdquo;Bakalár jednoodborového štúdia Aplikovaná informatika má ucelené znalosti z teoretickej informatiky a skúsenosti z efektívneho využívania informačných technológií na takej úrovni, aby bol akceptovaný na adekvátne pozície v rámci IT sektora. Z informatického hľadiska je pripravovaný tak, že dokáže implementovať, inštalovať a prevádzkovať zložité systémy, pracovať v implementačnom tíme pod vedením magistra/inžiniera informatiky. Dokáže rozširovať, prispôsobovať a lokalizovať systémy výpočtovej a informačnej techniky. Je zorientovaný vo viacerých oblastiach, kde nachádzajú uplatnenie riešenia z oblasti informatiky a informačných a komunikačných technológií&nbsp; a dokáže spolupracovať s používateľmi systému a špecialistami iných profesií. ...&ldquo; <span style=\"font-size:12px\"><a href=\"https://www.upjs.sk/prirodovedecka-fakulta/informacie-pre-zaujemcov-o-studium/studium/informatika-bakalarsky-aplikovana-informatika/\" target=\"_blank\"><span style=\"color:#2c3e50\">Zdroj</span></a></span></span></p><p style=\"margin-left:40px; text-align:justify\">&nbsp;</p><p style=\"margin-left:40px; text-align:justify\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Prijímacie konanie je realizované bez prijímacej skúšky na základe dosiahnutých študijných výsledkov z&nbsp;predchádzajúceho štúdia resp. strednej školy. Automatické prijatie na štúdium na UPJŠ je garantované pre uchádzačov, ktorých študijný priemer z&nbsp;polročného vysvedčenia v&nbsp;poslednom roku štúdia a&nbsp;z&nbsp;koncoročných vysvedčení v&nbsp;predchádzajúcich troch rokoch štúdia nepresiahol stanovený študijný priemer (2,00). V&nbsp;opačnom prípade je uchádzať hodnotený podľa výsledkov z&nbsp;maturitnej skúšky z&nbsp;predmetov Matematika a/alebo Informatika. Akceptovateľné sú takisto výsledky SCIO testov. Viac k&nbsp;podmienkam na prijatie <strong><a href=\"https://www.upjs.sk/public/media/2453/prij_konanie_PFUPJS_22_23_Bc.pdf\" target=\"_blank\"><span style=\"color:#16a085\">tu</span></a></strong> a <strong><a href=\"https://studijne-programy.upjs.sk/prihlaska/AIb?prihlasky=\" target=\"_blank\"><span style=\"color:#16a085\">tu</span></a></strong>.&nbsp;</span></p><p style=\"margin-left:40px; text-align:justify\">&nbsp;</p><p style=\"margin-left:40px; text-align:justify\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">UPJŠ je verejnou školou, a&nbsp;teda nevzniká povinnosť pre študenta platiť školné, ak sa rozhodne študovať dennou formou. Externé štúdium fakulta neponúka. Štúdium dennou formou je štandardne 3 roky.</span></p><p style=\"margin-left:40px; text-align:justify\">&nbsp;</p><p style=\"margin-left:40px; text-align:justify\"><strong><a href=\"http://www.upjs.sk/prirodovedecka-fakulta/informacie-pre-zaujemcov-o-studium/studium/informatika-bakalarsky-aplikovana-informatika/\" target=\"_blank\"><span style=\"color:#16a085\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Študijný program</span></span></a></strong></p><p style=\"margin-left:40px; text-align:justify\"><strong><a href=\"https://studijne-programy.upjs.sk/program/AIb\" target=\"_blank\"><span style=\"color:#16a085\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Študijný plán</span></span></a></strong></p>'),
(18, 14, 'Analýza dát a umelá Inteligencia', 'Áno', 'Nie', 'Slovenský', '18.4.2023', '<p style=\"margin-left:40px; text-align:justify\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">&bdquo;Absolvent študijného programu získa matematické základy nevyhnutné pre zvládnutie štatistických a&nbsp;optimalizačných metód potrebných pre inteligentnú analýzu dát. Bude mať tiež potrebné programátorské zručnosti, bude vedieť aplikovať metódy strojového učenia a&nbsp;vybrané metódy umelej inteligencie. Bude zvládať rôzne typy matematického softvéru a&nbsp;bude mať vedomosti z&nbsp;práce s&nbsp;veľkými objemami štruktúrovaných a&nbsp;neštruktúrovaných dát. Bude vybavený základnými &bdquo;soft&ldquo; zručnosťami a&nbsp;bude mať základné právne a&nbsp;etické povedomie o&nbsp;práci s&nbsp;dátami. Okrem toho bude poznať príklady aplikácií dátovej analýzy mimo matematiky a&nbsp;informatiky. ...&ldquo; <span style=\"font-size:12px\"><a href=\"https://www.upjs.sk/prirodovedecka-fakulta/informacie-pre-zaujemcov-o-studium/studium/moprog/ADUIb/\" target=\"_blank\"><span style=\"color:#2c3e50\">Zdroj</span></a></span></span></p><p style=\"margin-left:40px; text-align:justify\">&nbsp;</p><p style=\"margin-left:40px; text-align:justify\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Prijímacie konanie je realizované bez prijímacej skúšky na základe dosiahnutých študijných výsledkov z&nbsp;predchádzajúceho štúdia resp. strednej školy. Automatické prijatie na štúdium na UPJŠ je garantované pre uchádzačov, ktorých študijný priemer z&nbsp;polročného vysvedčenia v&nbsp;poslednom roku štúdia a&nbsp;z&nbsp;koncoročných vysvedčení v&nbsp;predchádzajúcich troch rokoch štúdia nepresiahol stanovený študijný priemer (2,00). V&nbsp;opačnom prípade je uchádzať hodnotený podľa výsledkov z&nbsp;maturitnej skúšky z&nbsp;predmetov Matematika a/alebo Informatika. Akceptovateľné sú takisto výsledky SCIO testov. Viac k&nbsp;podmienkam na prijatie <strong><a href=\"https://www.upjs.sk/public/media/2453/prij_konanie_PFUPJS_22_23_Bc.pdf\" target=\"_blank\"><span style=\"color:#16a085\">tu</span></a></strong> a <strong><a href=\"https://studijne-programy.upjs.sk/prihlaska/ADUIb?prihlasky=\" target=\"_blank\"><span style=\"color:#16a085\">tu</span></a></strong>. </span></p><p style=\"margin-left:40px; text-align:justify\">&nbsp;</p><p style=\"margin-left:40px; text-align:justify\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">UPJŠ je štátnou vysokou školou, a&nbsp;teda nevzniká povinnosť pre študenta platiť školné, ak sa rozhodne študovať dennou formou. Externé štúdium fakulta neponúka. Štandardná dĺžka štúdia pri prezenčnej forme je 3 roky.</span></p><p style=\"margin-left:40px; text-align:justify\">&nbsp;</p><p style=\"margin-left:40px; text-align:justify\"><a href=\"http://www.upjs.sk/prirodovedecka-fakulta/informacie-pre-zaujemcov-o-studium/studium/ADUIb/\" target=\"_blank\"><strong><span style=\"color:#16a085\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Študijný program</span></span></strong></a></p><p style=\"margin-left:40px; text-align:justify\"><strong><a href=\"https://studijne-programy.upjs.sk/program/ADUIb\" target=\"_blank\"><span style=\"color:#16a085\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Študijný plán</span></span></a></strong></p>'),
(19, 10, 'Hospodárska informatika', 'Nie', 'Nie', 'Slovenský', '19.4.2023', '<p style=\"margin-left:40px; text-align:justify\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">&bdquo;Výskum, vývoj a výučba na KKUI sa zameriava na základné princípy a metódy hospodárskej informatiky, analýzu rôznych typov dát (napr. medicínske, textové, webové, priemyselné, procesné, transakčné) s cieľom zefektívniť cieľový proces (napr. z hľadiska výpočtovej zložitosti, presnosti, ekonomickej efektívnosti),&nbsp; mobilné a webové aplikácie využívajúce najnovšie technologické trendy, riadenie IT služieb a ich podpora prostredníctvom softvérových riešení, projektový manažment, procesy návrhu a implementácie softvérových aplikácií, vytvorenie inteligentného priestoru vybaveného vhodnými senzormi, robotmi a ďalšími IKT zariadeniami, ktorý dokáže vnímať a rozpoznávať aktivity a udalosti v ňom a vhodne na ne reagovať, ...&ldquo; <span style=\"font-size:12px\"><a href=\"https://www.fei.tuke.sk/uploads/a1/92/a19266d9839e31db310dcd3d36128cec/Hospodarska-informatika-2019.pdf\" target=\"_blank\"><span style=\"color:#2c3e50\">Zdroj</span></a></span></span></p><p style=\"margin-left:40px; text-align:justify\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Technická Univerzita v&nbsp;Košiciach je verejnou školou, a&nbsp;teda študentovi nevzniká povinnosť platiť školné. Študijný program Hospodárska informatika je univerzitou v&nbsp;akademickom roku 2023/2024 ponúkaný len v&nbsp;dennej forme. Predpokladaný počet prijatých uchádzačov na daný študijný program je&nbsp; stanovený na 70 študentov.</span></p><p style=\"margin-left:40px; text-align:justify\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Prijímacie konanie sa koná bez prijímacej skúšky. Uchádzači budú prijatí na štúdium podľa bodového hodnotenia, ktoré získajú na základe výsledkov zo strednej školy, výsledkov písomnej maturitnej skúšky z&nbsp;matematiky z&nbsp;externej časti. Uchádzačom budú pridelené body takisto podľa typu strednej školy, ktorú vyštudovali a&nbsp;rovnako aj za úspešnú účasť v&nbsp;matematických, fyzikálnych a informatických olympiádach a&nbsp;súťažiach. Uchádzačovi, ktorý už v&nbsp;minulosti na TUKE študoval, avšak neúspešne a&nbsp;chce svoje šance skúsiť znovu, budú udelené mínusové body. Viac k&nbsp;bodovému hodnoteniu nájdete <strong><a href=\"https://www.fei.tuke.sk/uploads/60/57/60571667dce69a7a7891fac475777925/01b_PK-tabulka-hodnotenia-uchadzacov-bak-fei-2023-2024.pdf\" target=\"_blank\"><span style=\"color:#16a085\">tu</span></a></strong>. </span></p><p style=\"margin-left:40px; text-align:justify\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Pre prijatých uchádzačov sú pripravené <strong><a href=\"https://www.fei.tuke.sk/sk/studium/pre-uchadzacov/pripravne-kurzy\" target=\"_blank\"><span style=\"color:#16a085\">vzdelávacie kurzy</span></a></strong>, určené pre prvákov na doplnenie vedomostí z&nbsp;oblastí ako matematika, fyzika a&nbsp;elektrotechnika.</span></p><p style=\"margin-left:40px; text-align:justify\">&nbsp;</p><p style=\"margin-left:40px; text-align:justify\"><strong><a href=\"https://www.fei.tuke.sk/uploads/a1/92/a19266d9839e31db310dcd3d36128cec/Hospodarska-informatika-2019.pdf\" target=\"_blank\"><span style=\"color:#16a085\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Študijný program</span></span></a></strong></p><p style=\"margin-left:40px; text-align:justify\"><strong><a href=\"https://maisportal.tuke.sk/portal/studijneProgramy.mais?spsId=52509100&amp;arksId=47507289&amp;fakultaId=6878&amp;lang=sk\" target=\"_blank\"><span style=\"color:#16a085\"><span style=\"font-family:Tahoma, Geneva, sans-serif\">Študijný plán</span></span></a></strong></p>'),
(20, 10, 'Informatika', 'Nie', 'Nie', 'Slovenský', '19.4.2023', '<p style=\"margin-left:40px; text-align:justify\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">&bdquo;Absolvent bakalárskeho stupňa študijného programu Informatika má vedomosti z matematických a teoretických základov informatiky, algoritmizácie a údajových štruktúr, programovania, operačných a databázových systémov, používateľských rozhraní, webových a mobilných technológií a počítačových sietí. Pozná a rozumie princípom, metódam a vybraným technológiám v oblasti informatiky, softvérového inžinierstva, informačných systémov, počítačových systémov a sietí. Absolvent je schopný vyvíjať softvérové systémy, webové a mobilné aplikácie, dokáže implementovať, inštalovať, integrovať, rozširovať a prevádzkovať zložité počítačové systémy a pracovať v projektovom tíme. Dokáže spolupracovať s používateľmi počítačových systémov a so špecialistami z iných profesií. Voľbou voliteľných predmetov sa môžu študenti profilovať do oblasti softvérového inžinierstva, počítačových sietí, vývoja aplikácií na mobilných zariadenia alebo vývoja počítačových hier. Po skončení prvého stupňa štúdia bude absolvent pripravený na bezprostredný vstup na trh práce, respektíve na plynulé pokračovanie na druhom stupni štúdia v programe Informatika, kde má možnosť profilovať sa do rôznych oblastí - softvérové inžinierstvo, počítačové siete, počítačová bezpečnosť, používateľské rozhrania, spracovanie údajov.&ldquo; <span style=\"font-size:12px\"><a href=\"https://www.fei.tuke.sk/uploads/6e/9e/6e9ecfce807ece45dc50bec581157d07/Informatika-2019.pdf\" target=\"_blank\"><span style=\"color:#2c3e50\">Zdroj</span></a></span></span></p><p style=\"margin-left:40px; text-align:justify\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Technická Univerzita v&nbsp;Košiciach je verejnou vysokou školou, a&nbsp;teda študentovi nevzniká povinnosť platiť školné. Študijný program Informatika je univerzitou v&nbsp;akademickom roku 2023/2024 ponúkaný len v&nbsp;dennej forme. Predpokladaný počet prijatých uchádzačov na daný študijný program je&nbsp; stanovený na 200 študentov.</span></p><p style=\"margin-left:40px; text-align:justify\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Prijímacie konanie sa koná bez prijímacej skúšky. Uchádzači budú prijatí na štúdium podľa bodového hodnotenia, ktoré získajú na základe výsledkov zo strednej školy, výsledkov písomnej maturitnej skúšky z&nbsp;matematiky z&nbsp;externej časti. Uchádzačom budú pridelené body takisto podľa typu strednej školy, ktorú vyštudovali a&nbsp;rovnako aj za úspešnú účasť v&nbsp;matematických, fyzikálnych a informatických olympiádach a&nbsp;súťažiach. Uchádzačovi, ktorý už v&nbsp;minulosti na TUKE študoval, avšak neúspešne a&nbsp;chce svoje šance skúsiť znovu, budú udelené mínusové body. Viac k&nbsp;bodovému hodnoteniu nájdete <strong><a href=\"https://www.fei.tuke.sk/uploads/60/57/60571667dce69a7a7891fac475777925/01b_PK-tabulka-hodnotenia-uchadzacov-bak-fei-2023-2024.pdf\" target=\"_blank\"><span style=\"color:#16a085\">tu</span></a></strong>. </span></p><p style=\"margin-left:40px; text-align:justify\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Pre prijatých uchádzačov sú pripravené <strong><a href=\"https://www.fei.tuke.sk/sk/studium/pre-uchadzacov/pripravne-kurzy\" target=\"_blank\"><span style=\"color:#16a085\">vzdelávacie kurzy</span></a></strong>, určené pre prvákov na doplnenie vedomostí z&nbsp;oblastí ako matematika, fyzika a&nbsp;elektrotechnika.</span></p><p style=\"margin-left:40px; text-align:justify\">&nbsp;</p><p style=\"margin-left:40px; text-align:justify\"><strong><a href=\"http://www.fei.tuke.sk/uploads/6e/9e/6e9ecfce807ece45dc50bec581157d07/Informatika-2019.pdf\" target=\"_blank\"><span style=\"color:#16a085\"><span style=\"font-family:Tahoma, Geneva, sans-serif\">Študijný program</span></span></a></strong></p><p style=\"margin-left:40px; text-align:justify\"><strong><a href=\"https://maisportal.tuke.sk/portal/studijneProgramy.mais?spsId=52509104&amp;arksId=47507289&amp;fakultaId=6878&amp;lang=sk\" target=\"_blank\"><span style=\"color:#16a085\"><span style=\"font-family:Tahoma, Geneva, sans-serif\">Študijný plán</span></span></a></strong></p>'),
(21, 10, 'Inteligentné systémy', 'Nie', 'Nie', 'Slovenský', '19.4.2023', '<p style=\"margin-left:40px; text-align:justify\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">&bdquo;Študijný program Inteligentné systémy odboru Kybernetika ponúka vzdelávanie nielen v najnovších informatických technológiách ale kladie dôraz na ich využitie v aplikačných projektoch a výskume pre široké spektrum v oblasti inteligentných kybernetických systémov. Veľký dôraz kladieme okrem tém súčasnosti akými sú napr. klasická aplikovaná a priemyselná informatika, mikropočítače a vnorené systémy, automatizácia, databázové, webové a mobilné aplikácie, ale aj na vízie budúcnosti, akými sú napr. cloudová, priemyselná a sociálne robotika, umelá inteligencia, Smart Industry - big data, rozšírená realita, a senzorové siete, internet vecí a všetkého, kyberneticko-fyzikálne systémy, moderné metódy riadenia a diagnostiky, rozšírená realita.&ldquo; <span style=\"font-size:12px\"><a href=\"https://www.fei.tuke.sk/uploads/74/1f/741f5bc1656a7794e8fb65f0c95496d6/Inteligentne-systemy-2019.pdf\" target=\"_blank\"><span style=\"color:#2c3e50\">Zdroj</span></a></span></span></p><p style=\"margin-left:40px; text-align:justify\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Technická Univerzita v&nbsp;Košiciach je štátnou školou, a&nbsp;teda študentovi nevzniká povinnosť platiť školné. Študijný program Inteligentné systémy je univerzitou v&nbsp;akademickom roku 2023/2024 ponúkaný len v&nbsp;dennej forme. Predpokladaný počet prijatých uchádzačov na daný študijný program je&nbsp; stanovený na 70 študentov.</span></p><p style=\"margin-left:40px; text-align:justify\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Prijímacie konanie sa koná bez prijímacej skúšky. Uchádzači budú prijatí na štúdium podľa bodového hodnotenia, ktoré získajú na základe výsledkov zo strednej školy, výsledkov písomnej maturitnej skúšky z&nbsp;matematiky z&nbsp;externej časti. Uchádzačom budú pridelené body takisto podľa typu strednej školy, ktorú vyštudovali a&nbsp;rovnako aj za úspešnú účasť v&nbsp;matematických, fyzikálnych a informatických olympiádach a&nbsp;súťažiach. Uchádzačovi, ktorý už v&nbsp;minulosti na TUKE študoval, avšak neúspešne a&nbsp;chce svoje šance skúsiť znovu, budú udelené mínusové body. Viac k&nbsp;bodovému hodnoteniu nájdete <strong><a href=\"https://www.fei.tuke.sk/uploads/60/57/60571667dce69a7a7891fac475777925/01b_PK-tabulka-hodnotenia-uchadzacov-bak-fei-2023-2024.pdf\" target=\"_blank\"><span style=\"color:#16a085\">tu</span></a></strong>. </span></p><p style=\"margin-left:40px; text-align:justify\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Pre prijatých uchádzačov sú pripravené <strong><a href=\"https://www.fei.tuke.sk/sk/studium/pre-uchadzacov/pripravne-kurzy\" target=\"_blank\"><span style=\"color:#16a085\">vzdelávacie kurzy</span></a></strong>, určené pre prvákov na doplnenie vedomostí z&nbsp;oblastí ako matematika, fyzika a&nbsp;elektrotechnika.</span></p><p style=\"margin-left:40px; text-align:justify\">&nbsp;</p><p style=\"margin-left:40px; text-align:justify\"><strong><a href=\"https://www.fei.tuke.sk/uploads/74/1f/741f5bc1656a7794e8fb65f0c95496d6/Inteligentne-systemy-2019.pdf\" target=\"_blank\"><span style=\"color:#16a085\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Študijný program</span></span></a></strong></p><p style=\"margin-left:40px; text-align:justify\"><strong><a href=\"https://maisportal.tuke.sk/portal/studijneProgramy.mais?spsId=52509099&amp;arksId=47507289&amp;fakultaId=6878&amp;lang=sk\" target=\"_blank\"><span style=\"color:#16a085\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Študijný plán</span></span></a></strong></p>'),
(22, 10, 'Kyberbezpečnosť', 'Nie', 'Nie', 'Slovenský', '19.4.2023', '<p style=\"margin-left:40px; text-align:justify\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">&bdquo;Absolvent bakalárskeho stupňa študijného programu Kyberbezpečnosť má vedomosti z matematických a teoretických základov informatiky s aspektom na kyberbezpečnosť. Pozná a rozumie princípom, metódam a vybraným technológiám problematiky zabezpečenia počítačových systémov, počítačových sietí, organizácii a spoločnosti pred kybernetickými hrozbami. Absolvent je schopný identifikovať a riešiť bezpečnostné incidenty, realizovať bezpečnostnú architektúru a dátovú bezpečnosť, prevádzkovať a spravovať systémy s ohľadom na ich zabezpečenia, podieľať sa na projektovaní a auditoch bezpečnostných projektov a pracovať v projektovom tíme. Dokáže spolupracovať s používateľmi počítačových systémov a so špecialistami z iných profesií, pričom dokáže identifikovať a realizovať ich konkrétne požiadavky kyberbezpečnosti.&ldquo; <span style=\"font-size:12px\"><a href=\"https://www.fei.tuke.sk/uploads/e5/93/e59331418e0a6e8018772037ba2df1dd/Kyberbezpecnost-2019.pdf\" target=\"_blank\"><span style=\"color:#2c3e50\">Zdroj</span></a></span></span></p><p style=\"margin-left:40px; text-align:justify\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Technická Univerzita v&nbsp;Košiciach je štátnou školou, a&nbsp;teda študentovi nevzniká povinnosť platiť školné. Študijný program Kyberbezpečnosť je univerzitou v&nbsp;akademickom roku 2023/2024 ponúkaný len v&nbsp;dennej forme. Predpokladaný počet prijatých uchádzačov na daný študijný program je&nbsp; stanovený na 70 študentov.</span></p><p style=\"margin-left:40px; text-align:justify\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Prijímacie konanie sa koná bez prijímacej skúšky. Uchádzači budú prijatí na štúdium podľa bodového hodnotenia, ktoré získajú na základe výsledkov zo strednej školy, výsledkov písomnej maturitnej skúšky z&nbsp;matematiky z&nbsp;externej časti. Uchádzačom budú pridelené body takisto podľa typu strednej školy, ktorú vyštudovali a&nbsp;rovnako aj za úspešnú účasť v&nbsp;matematických, fyzikálnych a informatických olympiádach a&nbsp;súťažiach. Uchádzačovi, ktorý už v&nbsp;minulosti na TUKE študoval, avšak neúspešne a&nbsp;chce svoje šance skúsiť znovu, budú udelené mínusové body. Viac k&nbsp;bodovému hodnoteniu nájdete <strong><a href=\"https://www.fei.tuke.sk/uploads/60/57/60571667dce69a7a7891fac475777925/01b_PK-tabulka-hodnotenia-uchadzacov-bak-fei-2023-2024.pdf\" target=\"_blank\"><span style=\"color:#16a085\">tu</span></a></strong>. </span></p><p style=\"margin-left:40px; text-align:justify\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Pre prijatých uchádzačov sú pripravené <strong><a href=\"https://www.fei.tuke.sk/sk/studium/pre-uchadzacov/pripravne-kurzy\" target=\"_blank\"><span style=\"color:#16a085\">vzdelávacie kurzy</span></a></strong>, určené pre prvákov na doplnenie vedomostí z&nbsp;oblastí ako matematika, fyzika a&nbsp;elektrotechnika.</span></p><p style=\"margin-left:40px; text-align:justify\">&nbsp;</p><p style=\"margin-left:40px; text-align:justify\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><strong><a href=\"https://www.fei.tuke.sk/uploads/e5/93/e59331418e0a6e8018772037ba2df1dd/Kyberbezpecnost-2019.pdf\" target=\"_blank\"><span style=\"color:#16a085\">Študijný program</span></a></strong></span></p><p style=\"margin-left:40px; text-align:justify\"><strong><a href=\"https://maisportal.tuke.sk/portal/studijneProgramy.mais?spsId=52509093&amp;arksId=47507289&amp;fakultaId=6878&amp;lang=sk\" target=\"_blank\"><span style=\"color:#16a085\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Študijný plán</span></span></a></strong></p>'),
(23, 10, 'Počítačové modelovanie', 'Nie', 'Nie', 'Slovenský', '19.4.2023', '<p style=\"margin-left:40px; text-align:justify\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">&bdquo;Základom študijného programu Počítačové modelovanie je získanie teoreticko-metodologických vedomostí z kľúčových oblastí aplikovanej informatiky. Študijný program ponúka použitie teoretických výsledkov na simuláciu dejov a systémov s využitím moderných analytických metód pri navrhovaní a implementácii modelov pozostávajúcich z hotových modulov. So širokým teoretickým a všeobecným informatickým prehľadom doplneným o základné poznatky z oblasti elektrotechniky, aplikovanej informatiky, manažmentu, ekonomiky a z poznatkov umožňujúcich použiť primeranú teóriu, získa absolvent okrem iného aj schopnosť využívať aplikačný softvér pri navrhovaní modelov.&ldquo; <span style=\"font-size:12px\"><a href=\"https://www.fei.tuke.sk/uploads/e2/bc/e2bc507ced936b7f046638584a7057ae/Pocitacove-modelovanie-2019.pdf\" target=\"_blank\"><span style=\"color:#2c3e50\">Zdroj</span></a></span></span></p><p style=\"margin-left:40px; text-align:justify\">&nbsp;</p><p style=\"margin-left:40px; text-align:justify\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Technická Univerzita v&nbsp;Košiciach je štátnou školou, a&nbsp;teda študentovi nevzniká povinnosť platiť školné. Študijný program Počítačové modelovanie je univerzitou v&nbsp;akademickom roku 2023/2024 ponúkaný len v&nbsp;dennej forme. Predpokladaný počet prijatých uchádzačov na daný študijný program je&nbsp; stanovený na 20 študentov.</span></p><p style=\"margin-left:40px; text-align:justify\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Prijímacie konanie sa koná bez prijímacej skúšky. Uchádzači budú prijatí na štúdium podľa bodového hodnotenia, ktoré získajú na základe výsledkov zo strednej školy, výsledkov písomnej maturitnej skúšky z&nbsp;matematiky z&nbsp;externej časti. Uchádzačom budú pridelené body takisto podľa typu strednej školy, ktorú vyštudovali a&nbsp;rovnako aj za úspešnú účasť v&nbsp;matematických, fyzikálnych a informatických olympiádach a&nbsp;súťažiach. Uchádzačovi, ktorý už v&nbsp;minulosti na TUKE študoval, avšak neúspešne a&nbsp;chce svoje šance skúsiť znovu, budú udelené mínusové body. Viac k&nbsp;bodovému hodnoteniu nájdete <strong><a href=\"https://www.fei.tuke.sk/uploads/60/57/60571667dce69a7a7891fac475777925/01b_PK-tabulka-hodnotenia-uchadzacov-bak-fei-2023-2024.pdf\" target=\"_blank\"><span style=\"color:#16a085\">tu</span></a></strong>.&nbsp;</span></p><p style=\"margin-left:40px; text-align:justify\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Pre prijatých uchádzačov sú pripravené <strong><a href=\"https://www.fei.tuke.sk/sk/studium/pre-uchadzacov/pripravne-kurzy\" target=\"_blank\"><span style=\"color:#16a085\">vzdelávacie kurzy</span></a></strong>, určené pre prvákov na doplnenie vedomostí z&nbsp;oblastí ako matematika, fyzika a&nbsp;elektrotechnika.</span></p><p style=\"margin-left:40px; text-align:justify\">&nbsp;</p><p style=\"margin-left:40px; text-align:justify\"><strong><a href=\"https://www.fei.tuke.sk/uploads/e2/bc/e2bc507ced936b7f046638584a7057ae/Pocitacove-modelovanie-2019.pdf\" target=\"_blank\"><span style=\"color:#16a085\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Študijný program</span></span></a></strong></p><p style=\"margin-left:40px; text-align:justify\"><strong><a href=\"https://maisportal.tuke.sk/portal/studijneProgramy.mais?spsId=52509090&amp;arksId=47507289&amp;fakultaId=6878&amp;lang=sk\" target=\"_blank\"><span style=\"color:#16a085\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Študijný plán</span></span></a></strong></p>'),
(24, 10, 'Počítačové siete', 'Nie', 'Nie', 'Slovenský', '19.4.2023', '<p style=\"margin-left:40px; text-align:justify\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">&bdquo;V súčasnosti je vysoký dopyt nielen po programátoroch či vývojároch softvérových aplikácií, ale do popredia sa dostáva aj potreba odborníkov s komplexným vzdelaním a dôkladnými znalosťami informačných a komunikačných technológií, čo je práve cieľom študijného programu Počítačové siete. Okrem základných programátorských zručností získajú absolventi tohto študijného programu znalosti o princípoch počítačových a databázových systémov, ale najmä hlboké vedomosti o architektúrach počítačových sietí, ich aktívnych a pasívnych komponentoch a princípoch bezpečného prenosu informácie rôzneho druhu pevnými amobilnými sieťami.&ldquo; <span style=\"font-size:12px\"><a href=\"https://www.fei.tuke.sk/uploads/b7/c1/b7c155802602b86fd14e6505e8bb7507/Pocitacove-siete-2019.pdf\" target=\"_blank\"><span style=\"color:#2c3e50\">Zdroj</span></a></span></span></p><p style=\"margin-left:40px; text-align:justify\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Technická Univerzita v&nbsp;Košiciach je štátnou školou, a&nbsp;teda študentovi nevzniká povinnosť platiť školné. Študijný program Počítačové siete je univerzitou v&nbsp;akademickom roku 2023/2024 ponúkaný len v&nbsp;dennej forme. Predpokladaný počet prijatých uchádzačov na daný študijný program je&nbsp; stanovený na 130 študentov.</span></p><p style=\"margin-left:40px; text-align:justify\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Prijímacie konanie sa koná bez prijímacej skúšky. Uchádzači budú prijatí na štúdium podľa bodového hodnotenia, ktoré získajú na základe výsledkov zo strednej školy, výsledkov písomnej maturitnej skúšky z&nbsp;matematiky z&nbsp;externej časti. Uchádzačom budú pridelené body takisto podľa typu strednej školy, ktorú vyštudovali a&nbsp;rovnako aj za úspešnú účasť v&nbsp;matematických, fyzikálnych a informatických olympiádach a&nbsp;súťažiach. Uchádzačovi, ktorý už v&nbsp;minulosti na TUKE študoval, avšak neúspešne a&nbsp;chce svoje šance skúsiť znovu, budú udelené mínusové body. Viac k&nbsp;bodovému hodnoteniu nájdete <strong><a href=\"https://www.fei.tuke.sk/uploads/60/57/60571667dce69a7a7891fac475777925/01b_PK-tabulka-hodnotenia-uchadzacov-bak-fei-2023-2024.pdf\" target=\"_blank\"><span style=\"color:#16a085\">tu</span></a></strong>. </span></p><p style=\"margin-left:40px; text-align:justify\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Pre prijatých uchádzačov sú pripravené <strong><a href=\"https://www.fei.tuke.sk/sk/studium/pre-uchadzacov/pripravne-kurzy\" target=\"_blank\"><span style=\"color:#16a085\">vzdelávacie kurzy</span></a></strong>, určené pre prvákov na doplnenie vedomostí z&nbsp;oblastí ako matematika, fyzika a&nbsp;elektrotechnika.</span></p><p style=\"margin-left:40px; text-align:justify\">&nbsp;</p><p style=\"margin-left:40px; text-align:justify\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><strong><a href=\"http://www.fei.tuke.sk/uploads/b7/c1/b7c155802602b86fd14e6505e8bb7507/Pocitacove-siete-2019.pdf\" target=\"_blank\"><span style=\"color:#16a085\">Študijný program</span></a></strong></span></p><p style=\"margin-left:40px; text-align:justify\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><strong><a href=\"https://maisportal.tuke.sk/portal/studijneProgramy.mais?spsId=52509097&amp;arksId=47507289&amp;fakultaId=6878&amp;lang=sk\" target=\"_blank\"><span style=\"color:#16a085\">Študijný plán</span></a></strong></span></p>'),
(25, 2, 'Informatika', 'Áno', 'Nie', 'Slovenský', '22.4.2023', '<p style=\"margin-left:40px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">&quot;Štúdium na FIIT je koncipované tak, aby študenti získali najlepší základ a všeobecný prehľad o informatike a prehlbovali si svoje vedomosti podľa svojich záujmov. Práve preto otvárame jeden študijný program &ndash; Informatika.<br />\r\nV prvom ročníku študenti získajú všeobecný rozhľad a pochopia základné princípy, na ktorých budú stavať aj počas ďalšieho štúdia.<br />\r\nV druhom ročníku si začnú vyberať voliteľné predmety podľa želaného zamerania. Štúdium si môžu vyskladať podľa seba - úzko sa špecializovať na oblasť, ktorá ich zaujíma, alebo kombinovať predmety rôzneho zamerania a získavať tak širší prehľad.&quot; <span style=\"font-size:12px\"><a href=\"https://www.fiit.stuba.sk/prijimacky/bakalari/studijne-programy.html?page_id=1729\" target=\"_blank\"><span style=\"color:#2c3e50\">Zdroj</span></a></span></span></p><p style=\"margin-left:40px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Študent má v druhom ročníku na výber zo zameraní, ako <em>Softvérové inžinierstvo</em>, <em>Umelá inteligencia</em>, <em>Informačná bezpečnosť</em>, <em>Internet a internetové technológie</em>.</span></p><p style=\"margin-left:40px\">&nbsp;</p><p style=\"margin-left:40px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Štúdium daného študijného odboru je bezplatné. Štandardná dĺžka štúdia je 3 roky. Študijný program Informatika sa na FIIT STU vyučuje aj v 4-ročnej verzii, avšak v akademickom roku 2023/2024 ho fakulta neponúka.</span></p><p style=\"margin-left:40px\">&nbsp;</p><p style=\"margin-left:40px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><u>Fakulta na štúdium prijme uchádzačov, ktorí splnia niektoré z nasledujúcich kritérií</u> :&nbsp;</span></p>\r\n<ul style=\"margin-left:40px\">\r\n	<li><span style=\"font-family:Tahoma,Geneva,sans-serif\">dosiahnu percentil minimálne 70 z externej časti maturitnej skúšky z matematiky&nbsp;</span></li>\r\n	<li><span style=\"font-family:Tahoma,Geneva,sans-serif\">dosiahnu minimálne 70 bodov za diplom&nbsp;z matematickej, alebo informatickej súťaže</span></li>\r\n	<li><span style=\"font-family:Tahoma,Geneva,sans-serif\">dosiahnu minimálne 70 bodov z prijímacej skúšky (prijímacia skúška nie je povinná, ak spĺňajú podmienky prijatia v inej kategórii)</span></li>\r\n	<li><span style=\"font-family:Tahoma,Geneva,sans-serif\">potvrdí znalosť slovenského jazyka aspoň na úrovni B1 (týka sa len uchádzačov, ktorí maturujú mimo územia Slovenskej republiky a Českej republiky)</span></li>\r\n</ul>\r\n\r\n<p style=\"margin-left:40px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Viac k podmienkam prijatia na štúdium nájdeš <strong><a href=\"https://www.fiit.stuba.sk/prijimacky/bakalari.html?page_id=1677#podmienky\" target=\"_blank\"><span style=\"color:#16a085\">tu</span></a></strong>.</span></p>\r\n<p style=\"margin-left:40px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Prijímacia skúška pozostáva z úloh z matematickej aj informatickej oblasti. Okruhy tém otázok zo skúšky, priebeh skúšky a podrobnejšie info nájdeš <strong><a href=\"https://www.fiit.stuba.sk/prijimacky/bakalari/skuska.html?page_id=5763\" target=\"_blank\"><span style=\"color:#16a085\">tu</span></a></strong>.</span></p>\r\n<p style=\"margin-left:40px\">&nbsp;</p>\r\n<p style=\"text-align:center\"><iframe allow=\"accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture; web-share\" allowfullscreen=\"\" frameborder=\"0\" height=\"315\" src=\"https://www.youtube-nocookie.com/embed/AMJvbXE94nc\" title=\"YouTube video player\" width=\"560\"></iframe></p>\r\n<p style=\"text-align:center\">&nbsp;</p>\r\n<p style=\"margin-left:40px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><strong><a href=\"https://www.fiit.stuba.sk/prijimacky/bakalari/studijne-programy.html?page_id=1729\" target=\"_blank\"><span style=\"color:#16a085\">Študijný program</span></a></strong></span></p>\r\n<p style=\"margin-left:40px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><strong><a href=\"https://www.fiit.stuba.sk/buxus/docs/Studium/SP/SP2022-23_bak_INFO3.pdf\" target=\"_blank\"><span style=\"color:#16a085\">Študijný plán</span></a></strong></span></p>'),
(26, 7, 'Informatika', 'Nie', 'Neuvedené', 'Slovenský', '22.4.2023', '<p style=\"margin-left:40px; text-align:justify\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">&quot;Bakalársky program sme koncipovali tak, aby jeho absolvent dokázal zvládnuť stúpajúce nároky vedeckého, technického aj spoločenského vývoja. Teoretický základ štúdia poskytuje absolventom dlhodobo platné poznatky a formuje ich spôsob myslenia (exaktnosť, schopnosť abstrakcie). Základ štúdia je doplnený predmetmi, ktoré umožňujú hlbšiu špecializáciu v niektorej z oblastí informatiky, či už s&nbsp;cieľom nastúpiť po absolvovaní bakalárskeho štúdia informatiky do praxe alebo pokračovať vo zvolenej špecializácii na magisterskom štúdiu.&quot; <span style=\"font-size:12px\"><a href=\"https://fmph.uniba.sk/studium/programy/informatika/\" target=\"_blank\"><span style=\"color:#2c3e50\">Zdroj</span></a></span></span></p><p style=\"margin-left:40px; text-align:justify\">&nbsp;</p><p style=\"margin-left:40px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Štúdium študijného programu je bezplatné.</span></p><p style=\"margin-left:40px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><u>Uchádzač má možnosť byť automaticky prijatý na štúdium, ak splnil niektorú z nasledujúcich požiadaviek</u>:&nbsp;</span></p>\r\n<ul>\r\n	<li style=\"margin-left: 40px;\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">dosiahol výnimočný výsledok v niektorej z informatických/matematických súťaží</span></li>\r\n	<li style=\"margin-left: 40px;\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">má percentil aspoň 60 z autorizovanej skúšky z matematiky (SCIO&nbsp;test, externá časť maturity&nbsp;z matematiky, ...</span></li>\r\n	<li style=\"margin-left: 40px;\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">jeho koncoročná klasifikácia z predmetu matematika, alebo informatika je hodnotená, ako&nbsp;1, a to v každom ročníku&nbsp;strednej školy</span></li>\r\n</ul>\r\n\r\n<ul style=\"margin-left:40px\">\r\n</ul>\r\n\r\n<p style=\"margin-left:40px; text-align:justify\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Ak uchádzač nesplnil ani jedno z vyššie uvedených kritérií, bude hodnotený na základe dosiahnutých študijných výsledkov, resp. koncoročného hodnotenia z predmetov matematika a informatika. Prihliadať sa bude na hodnotenie v každom školskom roku. Ďalej bude uchádzač hodnotený na základe bodového zisku z externej časti maturitnej skúšky z predmetu matematika a informatika. Hodnotenie sa bude týkať aj dosiahnutých výsledkov zo SCIO testov z predmetov matematika/informatika, ak ich uchádzač absolvoval. Viac ku podmienkam na prijatie </span><a href=\"https://fmph.uniba.sk/prihlasky/\" style=\"font-family: Tahoma, Geneva, sans-serif;\" target=\"_blank\"><strong><span style=\"color:#16a085\">tu</span></strong></a><span style=\"font-family:Tahoma,Geneva,sans-serif\">. Viac o autorizovanej skúške z matematiky </span><a href=\"https://fmph.uniba.sk/studium/prijimacie-konanie/prihlasky/autorizovana-skuska-z-matematiky/\" style=\"font-family: Tahoma, Geneva, sans-serif;\" target=\"_blank\"><strong><span style=\"color:#16a085\">tu</span></strong></a><span style=\"font-family:Tahoma,Geneva,sans-serif\">.</span></p>\r\n<p style=\"margin-left:40px; text-align:justify\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Kto by mal záujem preriešiť si staré testy z matematiky, ktoré slúžili ako prijímacia skúška až do roku 2019, môže ich nájsť <strong><a href=\"https://fmph.uniba.sk/studium/prijimacie-konanie/prihlasky/prijimacie-skusky-zadania-a-riesenia/\" target=\"_blank\"><span style=\"color:#16a085\">tu</span></a></strong>.</span></p>\r\n<p style=\"margin-left:40px; text-align:justify\">&nbsp;</p>\r\n<p style=\"margin-left:40px; text-align:center\"><iframe allow=\"accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture; web-share\" allowfullscreen=\"\" frameborder=\"0\" height=\"315\" src=\"https://www.youtube-nocookie.com/embed/y6S8-ADJWS8\" title=\"YouTube video player\" width=\"560\"></iframe></p>\r\n<p style=\"margin-left:40px; text-align:justify\">&nbsp;</p>\r\n<p style=\"margin-left:40px; text-align:justify\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><strong><a href=\"https://fmph.uniba.sk/studium/programy/informatika/\" target=\"_blank\"><span style=\"color:#16a085\">Študijný program</span></a></strong></span></p>\r\n<p style=\"margin-left:40px; text-align:justify\"><strong><a href=\"https://sluzby.fmph.uniba.sk/infolist/sk/sp_INF.html\" target=\"_blank\"><span style=\"color:#16a085\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Študijný plán</span></span></a></strong></p>'),
(27, 7, 'Aplikovaná Informatika', 'Nie', 'Neuvedené', 'Slovenský', '22.4.2023', '<p style=\"margin-left:40px; text-align:justify\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Absolvent bakalárskeho študijného programu odboru aplikovaná informatika má dobrý základ znalostí z&nbsp;matematiky a teoretickej informatiky a zároveň široký prehľad o&nbsp;ich uplatnení a aplikácii v&nbsp;praxi (programovanie, databázy, programové systémy) a iných vedných odboroch (počítačová grafika, umelá inteligencia, kognitívna veda). Absolvent odboru aplikovaná informatika bude schopný pokračovať v&nbsp;štúdiu v&nbsp;zodpovedajúcom magisterskom programe alebo sa priamo uplatniť v&nbsp;IT priemysle. <span style=\"font-size:12px\"><a href=\"https://fmph.uniba.sk/studium/programy/aplikovana-informatika/\" target=\"_blank\"><span style=\"color:#2c3e50\">Zdroj</span></a></span></span></p><p style=\"margin-left:40px; text-align:justify\">&nbsp;</p><p style=\"margin-left:40px; text-align:justify\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Štúdium študijného programu je bezplatné.</span></p><p style=\"margin-left:40px; text-align:justify\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><u>Uchádzač má možnosť byť automaticky prijatý na štúdium, ak splnil niektorú z nasledujúcich požiadaviek</u>:&nbsp;</span></p>\r\n<ul style=\"margin-left:40px\">\r\n	<li style=\"text-align:justify\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">dosiahol výnimočný výsledok v niektorej z informatických/matematických súťaží</span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">má percentil aspoň 50 z autorizovanej skúšky z matematiky (SCIO&nbsp;test, externá časť maturity&nbsp;z matematiky, ...)</span></li>\r\n</ul>\r\n\r\n<p style=\"margin-left:40px; text-align:justify\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Ak uchádzač nesplnil ani jedno z vyššie uvedených kritérií, bude hodnotený na základe dosiahnutých študijných výsledkov, resp. koncoročného hodnotenia z predmetov matematika a informatika. Prihliadať sa bude na hodnotenie v každom školskom roku. Ďalej bude uchádzač hodnotený na základe bodového zisku z externej časti maturitnej skúšky z predmetu matematika a informatika. Hodnotenie sa bude týkať aj dosiahnutých výsledkov zo SCIO testov z predmetov matematika/informatika, ak ich uchádzač absolvoval. Viac ku podmienkam na prijatie <strong><a href=\"https://fmph.uniba.sk/prihlasky/\" target=\"_blank\"><span style=\"color:#16a085\">tu</span></a></strong>. Viac o autorizovanej skúške z matematiky <strong><a href=\"https://fmph.uniba.sk/studium/prijimacie-konanie/prihlasky/autorizovana-skuska-z-matematiky/\" target=\"_blank\"><span style=\"color:#16a085\">tu</span></a></strong>.</span></p>\r\n<p style=\"margin-left:40px; text-align:justify\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Kto by mal záujem preriešiť si staré testy z matematiky, ktoré slúžili ako prijímacia skúška až do roku 2019, môže ich nájsť <strong><a href=\"https://fmph.uniba.sk/studium/prijimacie-konanie/prihlasky/prijimacie-skusky-zadania-a-riesenia/\" target=\"_blank\"><span style=\"color:#16a085\">tu</span></a></strong>.</span></p>\r\n<p style=\"margin-left:40px; text-align:justify\">&nbsp;</p>\r\n<p style=\"margin-left:40px; text-align:center\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><iframe allow=\"accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture; web-share\" allowfullscreen=\"\" frameborder=\"0\" height=\"315\" src=\"https://www.youtube-nocookie.com/embed/y6S8-ADJWS8\" title=\"YouTube video player\" width=\"560\"></iframe></span></p>\r\n<p style=\"margin-left:40px; text-align:justify\">&nbsp;</p>\r\n<p style=\"margin-left:40px; text-align:justify\"><strong><a href=\"https://fmph.uniba.sk/studium/programy/aplikovana-informatika/\" target=\"_blank\"><span style=\"color:#16a085\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Študijný program</span></span></a></strong></p>\r\n<p style=\"margin-left:40px; text-align:justify\"><a href=\"https://sluzby.fmph.uniba.sk/infolist/sk/sp_AIN.html\" target=\"_blank\"><span style=\"color:#16a085; font-family:Tahoma,Geneva,sans-serif\"><strong>Študijný plán</strong></span></a></p>');
